css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://lkong.cn/"), url-prefix("http://m.lkong.cn/") {

	.module, .lkong_right, .lkongcontent, .post, .onefeed:hover, .tl .album, .tl .album img, .smallpagebar a:hover
	{ background-color: #121212 !important; }
	.sideCustom .pic img, .module .modulecon .isfollow, .quobes, .quotesmessage .addfollow, .ZebraDialog, .onefeed .avatar p[dataitem^="pm_"], .onefeed .avatar img, .postlist .user, .settingmain caption, .settingmain th, .settingmain .inputtext, .settingmain .inputtextarea, #mymiddleavatar, .redactor_editor, .redactor_editor:focus, .redactor_dropdown, #redactor_modal, #redactor_modal_inner, .postpage .post-forum ul li span, .st_post .st_post_title, .st_post .st_post_bar a, i.icon-circle-arrow-up, i.icon-circle-arrow-down, .threadratelog .metrobg, .smallpagebar a, .newfeed, .face_name .squarenum, .sideCustom .detail .ispunch, .module .modulecon .addfollow, .face_name.editsquare li div, .forumrightbtn .followstat a.isfollow, .th_rp_num, .lkong_hoho .title, .lkong_hoho .hoho .hohomain .onefeed .quotes, .onefeed .userextra, input.text
	{ background-color: #2D2D2D !important; }
	.nav-tabs > li > a
	{ background-color: #2D2D2D; }
	.lkongheader, .moduletitle h3, .new-feed-bar, .tl .users .name, .settingmain h3, .sidenav li.select, #redactor_modal header, .pane .pane_title, .threadratelog thead tr td, .smallpagebar a:first-child, .smallpagebar a:last-child, .forum_blackboard .title, .lkong_hoho .hohoclose, .lkong_hoho .title ul li.select, .forum_header .comment h5
	{ background-color: #2C3E50 !important; }
	.mylinks .btn, .nav-tabs > .active > a, .nav-tabs > .active > a:hover, .nav-tabs > .active > a:focus, .feed > .onefeed > div > span > a.addfollow, .nav-tabs > li > a:hover, .nav-tabs > li > a:focus, .input-button, .blue-button, td.userId a, .discoverlist ul li .content .addfollow, .redactortoolbar_div, #redactor_tabs a:hover, #redactor_tabs a.redactor_tabs_act, #redactor_tabs a, .redactor_modal_btn, .redactor_dropdown a[class^=" redactor_dropdown"]:hover, .pane_content .fastpost .textarea, .smallpagebar strong, li.guanzhu a.isfollow, .module .savesquare a.metrobg, .addsquare div:hover, .lkong_hoho .title ul li:hover, .showpane a, .lkong_hoho .hoho .hohomain .addfollow, .button_blue
	{ background-color: #CCCCCC !important; }
	.module .modulecon, .lkongfeed, .postlist .message, .pane_content, .forum_blackboard, .lkong_hoho, #login_form
	{ background-color: #181818 !important; }
	.tl .users .btn
	{ background-color: transparent !important; }
	.lkongheader, .module, .mylinks .btn, .addfollow, .lkongcontent, .tl .album, .tl .album img .tl .users .btn, .input-button, .blue-button, td.userId a, .discoverlist ul li .content .addfollow, .redactortoolbar_div, #redactor_modal, .redactor_dropdown a[class^=" redactor_dropdown"]:hover, .pane_content .fastpost .textarea, .pane_content .fastpost .textarea, .module .savesquare a.metrobg, .th_rp_num, .button_blue
	{ background-image: none !important; }
	
	[color="#000"], [color="#000000"], [color="Black"], .lkong_text, .userIntro
	{ color: #AAAAAA !important; }
	.lkong_quobes div, .sightml, .userIntro span:nth-of-type(2), .userIntro span:nth-of-type(3), .lkong_name .lkong_nickname, .nickname, .settingmain .tipmessage, #redactor_modal_close, .st_post .st_post_title [ID^="lou"], .threadratelog tr .ratetext, .sideCustom .detail .ispunch p, .sideCustom .detail .ispunch span, .inthread blockquote, .th_w1 .th_rp_num, .lkong_hoho .hoho .hohomain .onefeed .quotes, .onefeed .userextra, .module .modulecon .addfollow, #footer p
	{ color: #FFFFFF !important; }
	.mylinks .btn a, .W_gotop span, .nav-tabs > .active > a, .nav-tabs > .active > a:hover, .nav-tabs > .active > a:focus, .feed > .onefeed > div > span > a.addfollow, .nav-tabs > li > a:hover, .nav-tabs > li > a:focus, td.userId a, .discoverlist ul li .content .addfollow, .redactortoolbar_div a, #redactor_tabs a:hover, #redactor_tabs a.redactor_tabs_act, #redactor_tabs a, .redactor_modal_btn, .redactor_dropdown a[class^=" redactor_dropdown"]:hover, .smallpagebar strong, li.guanzhu a.isfollow, .module .savesquare a.metrobg, .addsquare div:hover a, .addsquare div:hover span, .lkong_hoho .title ul li:hover a span, .showpane a, .lkong_hoho .hoho .hohomain .addfollow, .W_gotop span em
	{ color: #000000 !important; }
	.onefeed a, .onefeed .datatitle, .module .modulecon .relate, .onefeed .avatar p[dataitem^="pm_"], .settingmain .inputtext, .settingmain .inputtextarea, .redactor_dropdown a, .postpage .post-forum ul li span, .smallpagebar a, .smallpagebar a:hover, .threadnum .configinfolist li a[dataitem^="forum_"] strong, .face_name.editsquare li div span, .forum_blackboard .content a font, .lkong_hoho .hoho .onefeed .content .lkong_row div span[style="color:#0099CD;font-weight: bold;"], input.text
	{ color: #E0BC2D !important; }
	.moduletitle h3, .new-feed-bar, .configinfolist li a span, .settingmain h3, .sidenav li.select a, #redactor_modal header, .threadratelog thead tr td, .smallpagebar a:first-child, .smallpagebar a:last-child, .threadratelog tr .allrate, .forum_blackboard .title h3, .forum_header .comment h5
	{ color: ORANGE !important; }
	
	[color="#00F"], [color="#0000FF"], [color="Blue"], [color="Navy"], [color="#000080"], [color="#0000F0"], [color="#0000ee"], .lkong-timestamp, .lkong_replynum, .userIntro span:nth-of-type(1), .st_post .st_post_title .other, .threadratelog tr .time, .threadnum .configinfolist li.posttime strong, .fm_info .time
	{ color: #3399FF !important; }
	.sideCustom h4 .userName, .lkong_row .lkong_username, .quotesmessage a[dataitem^="user"], .lkong_quobes a[href^="name"], .tl .users .name h2 a.lkhash, #manage_config_content td.userId, .configinfolist li a[dataitem^="user_"] strong, .fm_info .author a, .lkong_hoho .hoho .onefeed .content a[dataitem^="user_"]
	{ color: #2FC95C !important; }
	.forummod .th_w1 .vip, .lkong_hoho .hohoclose h3
	{ color: #E3232A !important; }
	.module .modulecon .isfollow, .forumrightbtn .followstat a.isfollow
	{ color: red !important; }
	.tl .users .btn
	{ display: none !important; }
	.module, .sideCustom .pic img, .lkongfeed, .nav-tabs > li > a:hover, .nav-tabs > li > a:focus, .nav-tabs > li > a, .tl .users .btn, .onefeed .avatar img, .input-button, .blue-button, .pane_content .fastpost .textarea, .button_blue
	{ border: 0 !important; }
	.redactor_color_link
	{ border: 2px solid #2D2D2D !important; }
	.module .modulecon .isfollow, .forumrightbtn .followstat a.isfollow
	{ border: 1px solid #FFFFFF !important; }
	.shadow
	{ border: 1px solid #CCCCCC !important; }
	.th_rp_num
	{ padding: 2px !important; }
  .smallpagebar strong
  { padding: 5px 6px 4px 7px !important; }
	.pane_content .fastpost .textarea
	{ line-height: 32px !important; height: 32px !important; }
	.pane_content .fastpost
	{ margin: 10px 25% !important; }
	.lkong_quobes div:nth-of-type(1), .forummod .datatitle, .lkong_hoho .hoho .onefeed .content
	{ font-size: 14px !important; }
	.st_post .st_post_message .inthread, .lkong_text, .th_rp_num
	{ font-size: 16px !important; }
	.onebtn
	{ white-space: nowrap !important; }

	body * { font-family: "Droid Sans Fallback", "苹方-简", "蘋方-簡", "蘋方-繁", "文泉驛微米黑", "WenQuanYi Micro Hei", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif; }
	body * { text-decoration: none !important; text-shadow: none !important; box-shadow: none !important; }
	html, body { color: #AAAAAA; background: #121212 !important; background-color: #121212 !important; background-image: none !important; }
	body h1, body h2, body h3, body h4, body h5, body h6, body ul, body li, body ol, body span, body p, body dt, body dd, body dl { background-color: transparent !important; }
	body h1, body h2, body h3, body h4, body h5, body h6, body th, body em, body b, body strong, label { color: #FFFFFF; }
	body div, body p	{ color: #AAAAAA; }
	body blockquote { color: #AAAAAA !important; background-color: #2D2D2D !important; background-image: none !important; }
	body textarea { color: #E0BC2D !important; background-color: #2D2D2D !important; background-image: none !important; }
	body table, body tr, body td { background-color: transparent; color: #AAAAAA; background-image: none !important; }
	body select, body option { background-color: #2D2D2D !important; color: #E0BC2D !important; }
	body input[type="text"] { color: #E0BC2D !important; background-color: #2D2D2D !important; }
	body button, body input[type="button"], body input[type="submit"] { color: #000000 !important; background-color: #CCCCCC !important; }
	body input[type="reset"], caption { color: ORANGE !important; background-color: #2C3E50 !important; }
	body a { color: #E0BC2D !important; }
	body a:link { color: #E0BC2D; }
	body a:visited { color: #AAAAAA; }
	body a:hover { color: #3399FF !important; }
	body a:active { color: #423F3F !important; }
	lightbox * { background: transparent !important; background-color: transparent !important; }
}
