css:
@-moz-document domain('www.repubblica.it'), url-prefix('http://www.repubblica.it') {
	/* 
		the code in here only apply to:
		-pages on the domain www.repubblica.it
		-pages whose URLs start with http://www.repubblica.it
	*/

.main-content {width: 969px !important;border-right: none !important}
.sub-content-1 {width: 730px !important}
.sidebar-content,
.has_domination .multimedia-wrapper,
#mirago-iframe,
.article.from-reptv,.article.from-sport,.article.from-donna,.article.from-motori,
.has_domination .adv-top,.adv,.adv-strip,
#home .page-header .manchette-left,#home .page-header .manchette-right{display: none !important}
body {background: none !important}

}
