css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.w-index.com/"), url("https://www.w-index.com/night.html"), url-prefix("https://www.w-index.com/world/"), url-prefix("https://www.w-index.com/bond/"), url-prefix("https://www.w-index.com/225/"), url-prefix("https://www.w-index.com/world/"), url-prefix("https://www.w-index.com/commodity/"), url-prefix("https://www.w-index.com/forex/"), url-prefix("https://www.w-index.com/news/"), url-prefix("https://www.w-index.com/pro/"), url-prefix("https://www.w-index.com/hikaku/") {

  #container h1, #nightbody, body, #container, .pickup, #tops, h3, .highslide, .subnavi, .sub-forex, #tool_wraper, #main-forex, #news-main, .news-sub, .headline, .bignews, .mininews, #main, #footer, .midashi2
  { background-color: #121212 !important; }
  
  .wchart, .usdjpy, .nydow, #footer, #navi li, #navi, .wchart-forex, .guusuu, .midashi th
  { background-color: #2D2D2D !important; }
  .nydow h3, .usdjpy h3, .wchart h3, #headerright li b, .wchart-forex h3, .wchart-225 h3, .hikakutitle th
  { background-color: #2C3E50 !important; }
  #navi a.select, #himg, #cse-search-box input[type="submit"]
  { background-color: #CCCCCC !important; }

  #tops, h3, #navi a.select, #container, #cse-search-box input, .side li, #navi, h1, #footer
  { background-image: none !important; }
  
  [color="#000"], [color="#000000"], [color="Black"]
  { color: #AAAAAA !important; }

  .wchart b, #copyright, #headerright li
  { color: #FFFFFF !important; }
  
  #navi li a.select, #cse-search-box input[type="submit"]
  { color: #000000 !important; }


  .nydow h3, .usdjpy h3, .wchart h3, #headerright li b, .wchart-forex h3, .wchart-225 h3 a
  { color: orange !important; }
  
  [color="#00F"], [color="#0000FF"], [color="Blue"], [color="Navy"], [color="#000080"], [color="#0000F0"], [color="#0000ee"]
  { color: #3399FF !important; }
  
  .green
  { color: #2FC95C !important; }
  
  .red
  { color: #E3232A !important; }
  
  #headerright li
  { font-weight: 700 !important; }

  
  #subwaku, #underbanner, #bigbanner, .pickup, .side_area, .pside, .fb_reset, .wchart_notice, #fb-root, #headerright li:nth-child(4), #headerright li:nth-child(5), .itx-widget, .sponcer
  { display: none !important; }
  
  .nydow
  { width: 212px !important; margin: 2px !important; padding: 0 !important; border-width: 0 1px !important; border: 1px solid #121212 !important; }
  .usdjpy
  { width: 212px !important; margin: 2px !important; padding: 0 !important; border-width: 0 1px !important; border: 1px solid #121212 !important; }
  .wchart
  { width: 212px !important; margin: 2px !important; padding: 0 !important; border: 1px solid #121212 !important; }
  .wchart, .usdjpy
  { height: 145px !important; }
  .wchart img, .usdjpy img, .nydow img
  { width: 202px !important; }
  #top
  { height: 190px !important; }
  #top, #header, #tops
  { border: 0 !important; }

  .usdjpy h3
  { margin: 0 !important; }
  
  #footer, #container
  { padding: 0 !important; }
  .usdjpy h3
  { margin: 0 !important; }
  
  #footer, #container
  { padding: 0 !important; }  
  #header, #navi
  { width: 92% !important; }
  #container, .chart_area, #main
  { width: 85% !important; }
  .chart_area, #main, #tops
  { width: 100% !important; }
  
  .wchart h3
  {
    text-overflow: ellipsis !important;
    overflow: hidden !important;
    white-space: nowrap !important;
  }  
  
  #himg
  { margin-left: 0 !important; }
  #headerright
  { padding-top: 0 !important; margin-top: 0 !important; }
  #navi li a, .wchart b, .wchart
  { font-size: 13px !important; }
  #headerright li:nth-child(1), #headerright li:nth-child(2), #headerright li:nth-child(3)
  { font-size: 20px !important; }

  body * { font-family: "苹方-简", "蘋方-簡", "蘋方-繁", "文泉驛微米黑", "WenQuanYi Micro Hei", "Droid Sans Fallback", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif; }
  body * { text-decoration: none !important; text-shadow: none !important; box-shadow: none !important; }
  html, body { color: #AAAAAA; background: #121212 !important; background-color: #121212 !important; background-image: none !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body ul, body li, body ol, body span, body p, body dt, body dd, body dl { background-color: transparent !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body th, body em, body b, body strong, label { color: #FFFFFF; }
  body div, body p  { color: #AAAAAA; }
  body blockquote { color: #AAAAAA !important; background-color: #2D2D2D !important; background-image: none !important; }
  body textarea { color: #E0BC2D !important; background-color: #2D2D2D !important; background-image: none !important; }
  body table, body tr, body td { background-color: transparent; color: #AAAAAA; background-image: none !important; }
  body select, body option { background-color: #2D2D2D !important; color: #E0BC2D !important; }
  body input { color: #E0BC2D !important; background-color: #2D2D2D !important; }
  body button, body input[type="button"], body input[type="submit"] { color: #000000 !important; background-color: #CCCCCC !important; }
  body a { color: #E0BC2D !important; }
  body a:link { color: #E0BC2D; }
  body a:visited { color: #AAAAAA; }
  body a:hover { color: #3399FF !important; }
  body a:active { color: #423F3F !important; }
  lightbox * { background: transparent !important; background-color: transparent !important; }
}
