css:
/* version: 0.4 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("my.byu.edu") {

body { 
  position: absolute; 
  left: -10000px !important;
  top: -10000px !important;
}

#portalWelcome {
  position: absolute;
  top: 10010px !important;
  left: 10040px !important;
  width: 600px !important;
}

#portalWelcome a {
  text-decoration: underline !important;
}

#whatisMyBYU {
  width: 50%;
}

#whatisMyBYU a {
  text-decoration: underline !important;
}

#whatisMyBYU, #launcherTreeMenu  {
  position: fixed;
  border: 5px solid;
  -moz-border-radius: 10px;
}

#whatisMyBYU {
  top: 25% !important;
  left: 25% !important;
  padding: 20px !important;
}

#launcherTreeMenu {
  top: 40px !important;
  left: 30px !important;
  right: 30px !important;
  padding: 20px 0 20px 20px !important;
}

#launcherTree li.expandable > ul {
  display: block !important;
}

#launcherTree > li {
  float: left;
  margin-right: 30px !important;
  margin-bottom: 20px !important;
}

#launcherTree span.folder {
  font-weight: bold !important;
  font-size: 110%;
  padding-left: 20px !important;
  text-decoration: underline;
}

#launcherTree a {
  font-weight: normal !important;
  font-size: 1.1em;
}

#launcherTree a:hover {
  text-decoration: underline !important;
}


/*[[colortheme]]*/

}

style_setting_options.value:
/* Light theme */

#launcherTreeMenu, #whatisMyBYU {
  border-color: #183874 !important;
  background-color: #fff !important;
}

#launcherTree a {
  color: #20304A !important;
}

#portalWelcome {
  color: #283C5D !important;
}

#portalWelcome a {
  color: black !important;
}

/* Dark theme */

html {
  background-color: #000 !important;
}

#whatisMyBYU {
  color: #ddd !important;
}

#whatisMyBYU h2, a {
  color: #fff !important;
}

#launcherTreeMenu, #whatisMyBYU {
  border-color: #333 !important;
  background-color: #222 !important;
}

#launcherTree span.folder {
  color: #ddd !important;
}

#launcherTree a {
  color: #ccc !important;
}
