css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.polyweekly.com") {

img {
  display: none;
}

body {
  background-color: #FFF !important;
  margin: 0px !important;
  padding: 12px 30px 4px 30px !important;
  font-size: 7pt !important;
}

a:link, a:visited {
  color: #00C !important;
  background-color: transparent !important;
  text-decoration: none !important;
  font-weight: normal !important;
}

a:hover, a.nav:hover, .titlebg a:hover, .titlebg2 a:hover {
  color: #C00 !important;
  background-color: transparent !important;
  text-decoration: none !important;
}


.nav, .nav:link, .nav:visited {
  color: #000 !important;
  background-color: transparent !important;
  text-decoration: none !important;
}

table {
  empty-cells: show !important;
}


td, th {
  background-color: #FFF !important;
  color: #000 !important;
  font-size: 7pt !important;
  font-family: verdana, sans-serif !important;
}

input, textarea {
  font-size: 9pt !important;
  color: #000000 !important;
  font-family: verdana, sans-serif !important;
  background-color: #FFF !important;
}


input.check {
  background-color: transparent !important;
}

select {
  font-size: 8pt !important;
  font-weight: normal !important;
  color: #000 !important;
  font-family: verdana, sans-serif !important;
  background-color: #FFF !important;
}

hr, .hrcolor {
  color: #0C0 !important;
  background-color: transparent !important;
}

.quote {
  color: #000 !important;
  background-color: #EEC !important;
  border: 1px solid black !important;
  margin: 1px !important;
  padding: 1px !important;
  font-size: x-small !important;
}

.code {
  color: #000 !important;
  background-color: #CCC !important;
  border: 1px solid black !important;
  margin: 1px !important;
  padding: 1px !important;
  font-size: x-small !important;
  line-height: 1.3em !important;
}

.quoteheader, .codeheader {
  color: #000000 !important;
  text-decoration: none !important;
  font-style: normal !important;
  font-weight: bold !important;
  font-size: x-small !important;
  line-height: 1.2em !important;
}

.meaction {
  color: red !important;
  background-color: transparent !important;
}

.editor {
  width: 100% !important;
}

.highlight {
  background-color: #EEC !important;
  font-weight: bold !important;
  color: black !important;
}

.windowbg {
  color: #000 !important;
  background-color: #DEF !important;
}

.windowbg2 {
  color: #000 !important;
  background-color: #EEF !important;
}

.titlebg, tr.titlebg th, tr.titlebg td, .titlebg a:link, .titlebg a:visited, .titlebg2, tr.titlebg2 th, tr.titlebg2 td, .titlebg2 a:link, .titlebg2 a:visited, .catbg, .catbg3 {
  font-weight: bold !important;
  font-style: normal !important;
  color: #000 !important;
  background-color: #CEF !important;
}

.bordercolor {
  background-color: #000 !important;
}

.tborder {
  border: 1px solid #000 !important;
}

.smalltext {
  font-size: x-small !important;
}

.normaltext {
  font-size: small !important;
}

.largetext {
  font-size: large !important;
}

img {
  display: none !important;
}

a img {
  border: 0 !important;
}

#ajax_in_progress {
  background: #CEF !important;
  color: #000 !important;
  text-align: center !important;
  font-weight: bold !important;
  font-size: 18pt !important;
  padding: 3px !important;
  width: 100% !important;
  position: fixed !important;
  top: 0 !important;
  left: 0 !important;
}

}
