css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("turntable.fm") {

.songlist .song:not(.topSong) {
  height: 22px !important;
  vertical-align:top !important;
}
.songlist .song:not(.topSong) .title, .songlist .song:not(.topSong) .details, .songlist .song:not(.topSong) .goTop, .songlist .song:not(.topSong) .playSample, .songlist .song:not(.topSong) .pauseSample {
  font-size: 0.65em !important;
  margin-top: -11px !important;
}

}
