css:
@-moz-document domain('pinboard.in'), url-prefix('http: //pinboard.in')

{body

{background: #FFFFFF !important;
font-family:arial;
margin-left:20px;
font-size:13px;
font-family: arial, sans-serif;
word-wrap:break-word;
color: #000000 !important;
margin: 0;}

a, a:link
{color: #6F4874 !important;}

a:hover
{color: #BF3070 !important;
text-decoration: none !important;}

h1, h2, h3, h4, .settings_heading, td.settings_heading
{background: #FFFFFF !important;
color: #2b1e03 !important;}

blockquote, code, .homepage_subheading
{color:  #8A003D !important}

#popup_header, .disabled, .count, #footer, #login
{color: #472902 !important;}

#organize_main
{color: #472902 !important;
width: auto !important;}

#organize_main .when, #organize_table tr td p span, #organize_table tr td span
{color: #472902 !important;}

#instructions
{color: #472902 !important;
width: 200px !important;
font-size: 0.9em !important;
opacity: 1 !important;}

#content
{margin-top: 0px;
clear: left;
display: inline-block;
height: 100%;
padding: 0px 15px;}

#content:after
{content: ".";
display: block;
clear: both;
visibility: hidden;
line-height: 0;
height: 0;}

html[xmlns] #content
{display: block;}

* html #content
{height: 1%;}

#banner
{top: 0px;
margin-top: 0px !important;
background: #80405C !important;
color: #80405C !important;
width: auto !important;
padding: 10px;
z-index: 100;
border-bottom: 1px solid #E6CFD9 !important;
-moz-border-radius-bottomright: 10px;
-moz-border-radius-bottomleft: 10px;}

#logo .pin_logo
{display: none;}

#logo #pinboard_name
{background: url(data:image/png;base64,) center left no-repeat;
height: 14px;
line-height: 14px;
padding-left: 16px;}

#timer
{color: #e8dddd !important;
font-family: arial, san-serif !important;
font-size: 0.7em !important;}

#banner a, #banner a.banner_username
{color: #FFFFFF !important;}

#banner a:hover, #banner a.banner_username:hover
{color: #CCCCCC !important;}

#main_column
{width: 730px !important;}

#main_column div p
{color: #472902 !important;}

.user_navbar, #nextprev
{color: #FFFFFF !important;}

.user_navbar a.selected
{color: #8A003D !important;
font-weight: bold !important;}

.user_navbar div, #main_column p b
{padding-left: 5px !important}

#nextprev
{margin-bottom: -13px !important;
padding-left: 5px !important;}

#bulk_edit
{background: transparent !important;}

#bulk_links #bulk_edit_options
{margin-top: 17px !important;}

#bulk_edit_box
{color: #000000!important;
background: #FFFFFF !important;
border: 1px solid #472902 !important;
-moz-border-radius: 10px !important;}

#bulk_top_bar
{background: #e8dddd !important;
border: 0 !important;
-moz-border-radius-topright: 10px;
-moz-border-radius-topleft: 10px;}

#confirm
{background: #382001 !important;
color:  #382001 !important;}

#confirm a
{color: #FFFFFF !important;}

#confirm a:hover
{color: #8A003D !important;}

#confirm_message
{color: #FFFFFF !important;}

#selected_count
{color: #8A003D !important;
font-weight: bold !important;}

.bookmark_count
{color: #472902 !important;}

.bookmark
{width: 700px !important;
border-bottom: 1px dashed #CCCCCC !important;
padding: 5px !important;
margin-bottom: 0 !important;}

.display
{width: 700px !important;}

.display br
{margin: 7px !important;}

a.bookmark_title
{color: #CF6795 !important;
font-size: 1.2em !important;
text-decoration: none !important;}

a.bookmark_title:visited
{color: #80596A !important;}

a.bookmark_title:hover
{color: #8A003D !important;}

.unread, a.unread
{background: #e8dddd !important;}

a.has_bmark, a.has_bmark:visited
{color: #FFBFEF !important;}

a.has_bmark:hover
{color: #8A003D !important;}

.description
{color: #000000!important;
line-height: 123% !important;
margin-bottom: 4px !important;
margin-top: -3px !important;}

.description a
{color: #CF6795 !important;}

.description a:visited
{color: #80596A !important;}

.description a:hover
{color: #8A003D !important;}

.description br
{margin: 0 !important;}

.bookmark a.tag, .source
{font-size: 0.8em !important;
border: 1px solid #CCCCCC !important;
padding: 2px 8px !important;
background: #FFFFFF !important;
-moz-border-radius: 10px !important;}

.bookmark a.tag:hover, .source:hover
{background: # e8dddd !important;
border-color: #472902 !important;}

.private
{background: #e8dddd !important;}

.private a.tag
{border-color: #e8dddd !important;}

a.when
{font-size: 0.8em !important;
color: #472902 !important;}

.star
{color: #e8dddd !important;
margin-left: 0;
padding-top: 5px !important;}

.selected_star
{color: #8A003D !important;}

a.edit, a.delete, a.mark_read
{font-size: 0.95em !important;
padding: 1px 4px !important;
border: 0 !important;}

a.url_link
{font-size: 0.95em !important;
float: right;
color: #CF6795 !important;
background-color: transparent !important;
font-weight: bold !important;}

a.url_link:hover
{color: #8A003D !important;}

#edit_bookmark_form
{color: #000000!important;
border: 0px solid !important;
background: #FFFFFF !important;
-moz-border-radius: 10px !important;}

#edit_bookmark_form p
{color: #472902 !important;
font-size: 0.8em !important;
padding-bottom: 2px !important;}

.edit_links
{margin: 0 10px 0 0 !important;
opacity: 1;
float: left;
text-align: left;}

.edit_links a
{color: #FFFFFF;}

.confirm
{color: #000000!important;}

.tabs
{background: #FFFFFF !important;
margin-left: 10px !important;
margin-top: 10px !important;}

.tab
{background: #FFFFFF !important;
margin-right: 10px;
padding-top: 3px;
border: 0 !important;}

.tab_selected
{background: #FFFFFF !important;
border: 1px solid #472902 !important;
border-bottom: 0 !important;
-moz-border-radius-topright: 10px;
-moz-border-radius-topleft: 10px;}

.tab_spacer
{display: none !important;}

#tab_panes
{color: #000000!important;
border: 1px solid #472902 !important;
background: #FFFFFF !important;
-moz-border-radius: 10px !important;}

#tab_panes p, #tab_panes p b, #tab_panes ol ol li p, .service_box p
{color: #000000!important;}

#tab_panes td
{background: #FFFFFF !important;}

.help, bookmarklet
{background: #e8dddd !important;}

#right_bar
{width: 285px !important;
background: #FFFFFF !important;
color: #472902 !important;
border: 1px solid #472902 !important;
float: right !important;
padding: 10px !important;
-moz-border-radius: 10px !important;}

#right_bar a
{border: 0 !important;
padding: 0 !important;
color: #CF6795 !important;
font-weight: bold;}

#right_bar a.tag
{font-weight: normal !important;}

#right_bar a.tag:visited
{color: #CF6795 !important;}

#right_bar a:hover, #right_bar a.tag:hover
{color: #8A003D !important;}

#right_bar a:visited
{color: #80596A !important;}

#tag_cloud
{float:left;
width:300px;
text-align:justify;
line-height:110%;
margin-top:20px;}

#tag_cloud div table tr td
{font-size: 80% !important;}

#tag_cloud #tag_cloud_header a.tag
{color: #CF6795 !important;
font-size: 100% !important;}

#tag_cloud_header
{color: #CF6795 !important;}

#tag_cloud_header p
{color: #FFFFFF !important;}

#tag_cloud_header a
{font-weight: normal;}

#tag_cloud_header a:link, #tag_cloud_header a:visited
{color: #472902 !important;}

#tag_cloud_header a:hover
{color: #8A003D !important;}

#tag_cloud a.tag_heading_selected
{color: #8A003D !important;
font-weight: bold !important;}

#tag_cloud a.selected
{background: #e8dddd !important;}

#right_column table tr td
{background: #FFFFFF !important;}

}
