css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: #ffffff !important}

#dashboard_controls_suggested_blogs {
background:#aaa !important;
border:none !important
}

#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background:rgba(0,0,0,0.2) !important}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag, #main_tag_beta {background:#000 url(http://static.tumblr.com/r5fvnsu/kw0lt3an4/bgg.jpg) no-repeat bottom right fixed !important}

#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current {background-image:none !important}

#footer,.dashboard_options_form_divider {display:none}

#left_column #posts .post .permalink {background-image: url('http://i51.tinypic.com/vdmjyr.jpg') !important;}

#left_column, #dashboard_controls a, #left_column #dashboard_controls span {color: #5d676a !important;}

#left_column #posts .notification {background-color:#e6e7ec !important;
border-bottom:none !important; border-top:none !important; border-left:none !important; border-right:none !important; color:#aaa !important}

#left_column #posts .notification .avatar {background-color:#0b1023 !important; border-color:transparent !important; opacity:0.8 !important}

#left_column #posts .notification .avatar:hover {opacity:98 !important}

#left_column #posts .notification .block,.post_info,.post_info a {color:#aaa !important}

#left_column #posts .notification a {border:none !important; color:#888 !important}

#left_column #posts .notification blockquote {border-left-color:#bbb !important}

#left_column #posts .notification.alt, #dashboard_switch_blog_menu_footer, #dashboard_switch_blog_menu_wrapper {background-color:#ffffff !important}

#left_column #posts .post .post_avatar, #left_column #posts .post .sub_avatar .sub_avatar_inner {background-color:#0b1023 !important;
border-bottom:none !important}

#left_column #posts .post, .dashboard_options_form {border-bottom:none !important}

#new_post a{color:#abb0c3 !important}
#new_post a:hover{color:#ffffff !important}

.arrow{display:none !important}

ol#posts li.post.new_post {background: #0e142c url('http://static.tumblr.com/r5fvnsu/S0alt3ayl/up.jpg') center center no-repeat !important;}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{width:0; height:70px; padding-right:250px; visibility: hidden;}

#nav .nav_item .nav_menu a {border-top-color:#eee !important;
color:#888 !important}

#nav .nav_item .nav_menu a:hover, .no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span, #posts .post .footer_links a:hover, #posts .post .post_controls a:hover, #right_column, #right_column a, .dashboard_nav_title, .dashboard_subpages, .dashboard_controls_suggested_blog_title, .dashboard_controls_suggested_blog_description {color:#5d676a !important}

#nav .nav_item .nav_menu, #dashboard_switch_blog_menu_header {background-color:#f7f7f7 !important; border:none !important}

#nav .nav_item .active, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel, #right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {background:#283544 !important}

#pagination a {background: #888 !important;color: #eee !important}

#pagination a:hover {background: #666 !important}

#posts .post .footer_links a, #posts .post .post_controls a {color:#a2adb2 !important}

#right_column .dashboard_nav_item {background:none !important; border-bottom:1px solid #aaa !important}

#right_column a:hover, .new_post_label:hover {color:#abb0c3 !important}

#show_radar, #hide_radar, #hide_recommended_blogs, #show_recommended_blogs {background:none !important}

.dashboard_controls_radar_media {background:#aaa !important}

.dashboard_controls_suggested_blog {border:1px solid #999 !important; border-width:0 0 1px !important}

.dashboard_switch_blog_menu_item {background-color:#ececec !important; border:0 !important}

.nav_item:hover {color:#eee !important}

.new_post_label {-webkit-text-stroke:0 !important; color:#aaa !important; opacity:2 !important; bottom: 0px !important; paddin bottom: 80px; height: 17px; border-radius: 6px}

.notification_type_icon {display:none !important}

.sub_avatar {background-color:transparent !important; height:32px !important; width:32px !important}


h1,h2,h3,h4,h5,h6{color:#949a9d !important}

.new_post_label{text-transform: uppercase !important; font-family:trebuchet ms !important; font-size: 10px !important; font-style:normal !important;
font-weight:bold !important; letter-spacing:1px !important; color:#ccc !important; background-color:rgba(0,0,0,0.2) !important; padding:0px !important; margin-top:5px !important; text-shadow: 1px 1px 1px #2f2f2f !important;}
}
