css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("clip.livedoor.com") {
  #functionHeader,
  #megaText,
  #contentHeader p + ul *,
  .category-menu-box,
  #sideadbnr,
  div.side-login-wrapper + div.side-normal-box,
  #footermenu-b-t,
  #footer,
  #headerBanner,
  .AdsBox,
  .panorama-banner
  {
    display: none;
  }
  .ads_bottom.bg_white {
    display: none;
  }
  .global-menu-box {
    margin-top: -35px !important;
  }
  .personal-td-right.pxsmall {
    padding-right: 80px !important;
  }
  #wrapper {
    margin-top: -43px;
  }
  #htmltagcloud span {
    margin: 0;
    padding: 0;
    font-size: 14px !important;
    font-weight: normal !important;
  }
  #htmltagcloud span a:link {
    color: #0090bb !important;
  }
  #htmltagcloud span a:hover,
  #htmltagcloud span a:active,
  #htmltagcloud span a:focus {
    color: white !important;
    background-color: #00C0FF !important;
  }
}
