css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lavabit.com") {
    /* making every background dark gray and every character light gray */
    html,tbody,td,td b a,td a {
        background: #333 !important;
        color: #aaa !important;
    }

     /* making the unread e-mail yellow without making other stuff yellow */
     .gray b { color: #cc0 !important; }
     .gray b small { color: #aaa !important; }
     .gray b a { color: #ff0 !important; }
     .white b { color: #cc0 !important; }
     .white b small { color: #aaa !important; }
     .white b a { color: #ff0 !important; }
}
