css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bikerswitchboard.com") {

a:link [id="xg_sitename"]{font-size:2.5em !important;}
p[class="xj_site_desc"]{color: #ffe081!important; font-size:2.0em!important;}

body {background-image: url("http://forums.bs-raven.net/Themes/RavenWood/images/body_wood_bg.jpg")!important; background-attachment: fixed !important; background-repeat: repeat !important; background-position: top left !important; font-family: "Book Antiqua", "Palatino Linotype", "Comic Sans MS" !important; font-size: 12px; color: #ffffff !important;}

div#xg_body, select, strong,
div#xg, textarea, input[class="textfield"],
div[class="input-container xj_autogrow_parent"]
{background-image: url("http://forums.bs-raven.net/Themes/RavenWood/images/trans_back.png")!important; opacity:.9 !important; -moz-border-radius: 5px!important; -webkit-border-radius: 5px; background-repeat: repeat !important; } 


/*---- Misc things on pages, Birthdays etc. ------- */
div [class="xg_module topmsg birthday_msg"]{background: transparent !important; border-color: #FFE081 !important;}
p[class="last-child"]{color: green !important; font-size: 16px !important;}
.button, button[class="xj-new-feed-items xg_show_activity_items"], button[class="xj_see_more xg_show_activity_items"]{background: green !important;}

 /*------ Links ----*/
a:link, a:visited, a:active, a{font-family: "Book Antiqua", "Palatino Linotype", "Comic Sans MS" !important; color: #ffe081!important; font-size:1.0em!important;}

 /*----Links on Hover----*/
 a:hover{color: #990000!important; font-size: 11px; text-decoration: none !important; background-image: url(http://forums.bs-raven.net/Themes/RavenWood/images/catbg.gif)!important; -moz-border-radius: 5px !important; -webkit-border-radius: 5px !important;}

/*---- Main Menu ----- Modules & Headers -----*/
h2{color: #ffffff !important; font-size: 12px !important;}

div [class="feed-reactions"],
a[class="button"],
div#xg_navigation, 
div [class="xg_module_head"], 
div [class="xg_module_head handle"], 
div [class="xg_module_body"], 
div [class="xg_module_body body_calendar"], 
div [class="xg_module_body body_list"],
div [class="xg_module_body body_detail"], 
div [class="xg_module_body body_large"], 
div [class="xg_module_body xg_user_generated"], 
div [class="xg_module_body account-links"],
div [class="xg_module_body body_small"], 
#xj_see_more see_more_cta
{background-image: url("http://i1230.photobucket.com/albums/ee498/QuanDotcom/Black_Background_Set_Wood_by_iAmFreeman.jpg")!important; -moz-border-radius: 5px !important; -webkit-border-radius: 5px !important;}

/*---- Forms & Buttons ----*/

input[class="button xj_submit"],
button[class="xj_submit feed-action-button"],
input[id="xj_comment_submit"]
{background-image: url("http://i1230.photobucket.com/albums/ee498/QuanDotcom/Black_Background_Set_Wood_by_iAmFreeman.jpg")!important; -moz-border-radius: 5px !important; -webkit-border-radius: 5px !important;}

div [class="module module-feed"],
 select, input[class="textfield"],
input[id="xj_comment_submit"],
button[class="xj_submit feed-action-button"],
button[class="xj_see_more see_more_cta"],
input[class="goto_button button"]{background: transparent !important;}

input[class="goto_button button"], textarea, select, input,
button[class="xj_submit feed-action-button"],
button[class="xj_see_more see_more_cta"],
input[class="button xj_submit"]{border-color: #FFE081 !important;}

textarea, select, input[class="textfield"]{color: #ffffff!important;}

/*---- block the ad boxes -----*/
div [id="xg_foot"], #xg_ad_below_header, #xg_ad_above_header {display: none !important;}
#xg_ad_below_footer {display: none !important;}
div [class="xg_module html_module module_text xg_reset"]{display: none !important;}

}
