css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){

.speeddial-container {
    border-radius: 3px !important;
    outline: rgba(10,10,10,0.1) 1px solid !important;
}
/*[[dialtitleposition]]*/
.bookmark-item {
    -moz-margin-start: 3px !important;
    margin-top: 0px !important;
}

.speeddial-top-middle {
	padding: 2px 0 2px 0 !important;
}

.speeddialThumb {
    margin: 3px !important;
    border: 1px rgba(0,0,0,0.2) solid;
    border-radius: 6px !important;
    padding: 1px;
    background: #fff;
    box-shadow: 0px 1px 1px rgba(10,10,10,0.1);
    font-size: 1em;
    /*font-weight: bold;*/
}

.speeddialThumb[assigned="f"] {
    opacity: 0 !important;
}

.speeddialThumb[assigned="f"]:hover {
    opacity: 100 !important;
}

.speeddialThumb:hover{
    background:-moz-linear-gradient(rgba(34,111,154,1), rgba(63,132,174,1),
    rgba(51,102,154,1), rgba(51,102,154,1), rgba(51,102,154,1),rgba(51,102,154,1),
    rgba(51,102,154,1)) !important; 
}

.thumbnail-title {
    text-align: center;
}

.speeddialThumb:hover .speeddial-top-middle {
    color: #fff !important;
}

.speeddial-top-middle img {
    display: none !important;
}

#speedDialPanel1-rows {
    padding-top: 0px
}

.speeddial-top-middle {
    background: none !important;
    position: relative !important;
    left: 200px !important;
}

.speeddial-top-left,.speeddial-top-right,
.speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right
{
    display: none;
}

#speeddialWindow {
    background: #f1f1f1 !important;
}

.speeddial-container {
    margin: 0 !important;
    border: 0 !important;
}

}

style_setting_options.value:
/* reverse enable dial title on bottom */
.thumbnail {
    -moz-box-direction: reverse !important;
}
