css:
@-moz-document domain(tumblr.com) {
body {
  background:url("http://27.media.tumblr.com/tumblr_lmegi8rWWT1qf7n84o1_500.png") repeat fixed !important;
  background-color: rgba(0,0,0, 0.95) !important;
  background-position: 0% 0% !important;
}



#logo {
  width: 0px !important;
  height: 0px !important;
  background:url("http://i53.tinypic.com/2ikbdac.png") !important;
  padding-left: 196px !important;
  height: 44px !important;
}


#default_tabs  LI  A  SPAN:first-child,
#user_channels LI  A SPAN {
  color:rgba(0,0,0,1) !important;
}
#default_tabs  LI  A  SPAN:first-child:hover,
#user_channels LI  A SPAN:hover {
  color: rgba(255,255,255,1) !important;
}
#default_tabs  LI[class^="tab selected"]  A  SPAN:first-child,
#user_channels LI[class^="tab selected"]  A SPAN  {
  color: rgba(0,0,0,1) !important;
}
.tab.iconic {
  background: rgba(0,0,0,0.3) !important;
  height: 10px !important;
  border-radius: 20px !important;
}


img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
opacity: 0.00 !important;  
}
#new_post{
  background-image: url(http://i56.tinypic.com/2uj0174.png) !important;
  background-position: center center !important; 
  background-repeat: no-repeat !important;
}



#new_post {
  background-color: rgba(255, 255, 255, 1.0) !important;
}



#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(0,0,0, 0.3) !important;
}
 


#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 0.9) !important;
}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(0,0,0, 0.3)  !important;
}



li.selected, li.recessed, a.active, div.post_filter_tabs a {
  background: none !important;
}



.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(255,255,255, 0.7) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}



.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
}
