css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("getnews.jp") {

/* Non-display part */
iframe,
#searchForm,
#tab_menu,
#footer_bg,
#leftColumn,
#rightColumn,
#foot,
#ft_bg,
#clm_m_head,
#clm_m_end,
#clm_m_body > div.pagebar,
#clm_m_body > div.pagebar + div,
#clm_m_end + #clm_m_head + #clm_m_body,
#adingoBeagle1,
#bookmark_single,
#adft_border
{ display: none !important; }

/*#disqus_thread*/

/* Display part */
body,
.container,
#mainContent,
#mainColumn,
#clm_m_body,
.post-bodycopy
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

}
