css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.appps.jp") {

/* Non-display part */
#aside,
#footer-outer,
.article-pager,
.article-cal,
.social-parts,
#ad2,
#article-options,
.article-body-more > ins
{ display: none !important; }

/* Display part */
body,
#container,
#content,
#main,
.article-outer,
.hentry,
.article-header,
.article-body,
.entry-content,
.article-body-inner,
.article-body-more
{
    width: 100% !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    margin: 0 0 0 0 !important;
}

}
