css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("labaq.com") {

/* Non-display part */
#outfooter,
#texttitle,
#logobase,
#footer,
#right,
#left,
#contentcommentbottom,
.pager,
#ad_rs,
.blogbodybottom,
.lq_stb_block,
.adsection,
#ad2,
.adsection_left,
#lq_st,
.navigate,
.posted,
.article_tags,
.comblogbodybottom,
.amazlet-box,
#ArticleBottom
{ display: none !important; }

/* Display part */
body,
#container,
#banner,
#blogcontainer,
#wrapper,
#content,
#blog,
#fullbody,
#blogbody,
#main,
#mainmore
{
    width: 100% !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

#banner
{
    width: auto !important;
    height: 18px !important;
    background: #222288 !important;
    color: #ffffff !important;
    padding: 10px 10px 10px 10px !important;
    font-size: 24px !important;
}

#banner:after
{
    content: "らばＱ";
}

}
