css:
/* FIREFOX START */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("neogaf.com") {

/*[[userhighlighter]]*/

a:hover{
color:#FF8B01 !important;
}

#links a:hover{
color: #FF8B01 !important;
}

/* TOP HEADER */
#header { 
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_header.png) !important;
background-size: 400px 140px;
}

/* WELCOME PANEL */
#header-user {
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_user_header.png)left top no-repeat !important;
text-shadow: 1px 1px 2px black !important;
background-size: 500px 75px;
height:50px;
}
.user-time .time {
color: #FF8B01 !important;
}
/*Added*/
.user-welcome:before {
Content: "Hello! ";
font-weight:bolder;
}

/* HEADER NAV (User CP, etc) */
#header-nav {
position:absolute;
top:-22px;
/*right:300px;*/
left:0px;
}
#header-nav li a{
font-size:90% !important;
color:#c5c5c5 !important;
text-shadow: 1px 1px 2px black !important;
}
#header-nav li a:hover{
color:#FF8B01 !important;
}
div a[href*="do=logout"]{
/*for later use*/
}


/* GOOGLE SEARCH */
#header-search {
position:absolute;
right:0px;
top:50px;
}


/* AD BAR TOP */
.special-bar-line{
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_ad_bg.png") !important;
}
div[id="google_ads_div_NeoGAF_thread_list_top_728_90_ad_container"]{
/*position:absolute;*/
/*bottom:-6px;*/
/*left:500px;*/
/*doesn't work nicely, yet :/*/
}


/* AD BAR BOTTOM */
div[id="google_ads_div_NeoGAF_thread_list_bottom_728_90_ad_container"]{
/*for later use*/
}


/* NEOGAF LOGO */
#header h1#header-logo a{
display:block;
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_logo.png") no-repeat !important;
background-size:100% !important;
height:85px !important;
float: left;
margin: -10px 0 0 0px !important;
position: relative;
}


/* FORUM TOOLS Menu */
.vbmenu_control {
text-shadow: 1px 1px 2px black !important;
}


/* PAGE BACKGROUND */
#contentwrap{
background:#c5c5c5 !important;
}
.page{
background:#c5c5c5 !important;
}


/* MAIN TABLE BORDER */
.tborder {
background: #c0c0c0 !important;
border-color: #c0c0c0 !important;
border:1px solid grey !important;
box-shadow: 0px 3px 7px #888888 !important;
}
/* TABS - General */
.forum-tabs{
top:2px !important;
}
.forum-tabs.bot *{
bottom:2px !important;
}

/* TABS - Active */
.forum-tabs li.activetab a,
.forum-tabs a:hover
{ 
height:21px !important;
width: 118px !important;
margin-left:10px;
color:#FF8B01 !important;
line-height:22px !important;
text-shadow: 1px 1px 2px black;
font-weight:bold;
background:url("http://dl.dropbox.com/u/23940693/gaf/tab_active.png") no-repeat !important;
}
/* TABS - All hover*/
.forum-tabs *:hover,
.forum-tabs.bot *:hover{
color:#FF8B01 !important;
}

/* Arrow part (not used) */
.forum-tabs li{
background-image:none !important;
margin:0 0 0 0 !important;
padding: 0 8px 0 0 !important;
z-index:2 !important;
}

/* TABS - Inactive*/
.forum-tabs li a
{
height:21px;
width: 118px !important;
margin-left:10px;
padding:0 10px 0 10px !important;
line-height:22px !important;
/*font-weight:bold;*/
opacity:100 !important;
color:#333 !important;
text-shadow: 1px 1px 1px grey;
border-radius: 0 !important;
background:url("http://dl.dropbox.com/u/23940693/gaf/tab_inactive.png") no-repeat !important;
}
/* TABS - Child Active*/
.forum-tabs li.child.activetab a,
.forum-tabs li.child a:hover
{
height:17px !important;
width:90px !important;
line-height:17px !important;
position:relative;
top:4px !important;
font-size:100% !important;
background: url("http://dl.dropbox.com/u/23940693/gaf/tab_child_active.png") no-repeat !important;
}

/* TABS - Child Inactive*/
.forum-tabs li.child a
{
height:17px;
width:90px !important;
margin-left:-45px !important;
line-height:17px !important;
position:relative;
top:4px !important;
padding: 0 0 0 18px !important;
font-weight:normal;
font-size:100% !important;
z-index:1;
background:url("http://dl.dropbox.com/u/23940693/gaf/tab_child_inactive.png") no-repeat !important;
}

/* TABS - Bottom Active */
.forum-tabs.bot li.activetab a,
.forum-tabs.bot a:hover
{
color:#FF8B01 !important;
text-shadow: 1px 1px 2px black;
background:url("http://dl.dropbox.com/u/23940693/gaf/tab_bottom_active.png") no-repeat !important;
}
/* TABS - Bottom Inactive */
.forum-tabs.bot li a {
opacity:100 !important;
color:#333 !important;
text-shadow: 1px 1px 1px grey;

background:url("http://dl.dropbox.com/u/23940693/gaf/tab_bottom_inactive.png") no-repeat !important;
}
/* TABS - Bottom Child Inactive */
.forum-tabs.bot li.child a{
position:relative;
top:0px !important;
font-size:100% !important;
background:url("http://dl.dropbox.com/u/23940693/gaf/tab_bottom_child_inactive.png") no-repeat !important;
}
/* TABS - Bottom Child Active */
.forum-tabs.bot li.child.activetab a,
.forum-tabs.bot li.child a:hover
{
position:relative;
top:0px !important;
line-height:17px !important;
height:17px !important;
font-size:100% !important;
width:90px !important;
background:url("http://dl.dropbox.com/u/23940693/gaf/tab_bottom_child_active.png") no-repeat !important;
}

/* THREAD PAGE NUMBERS */
.alt1 .smallfont, .alt1Active .smallfont, .alt2 .smallfont, .alt2Active .smallfont{
}


/* THREADS HEADER BAR ("Threads in Forum") */
.tcat{
text-shadow: 1px 1px 2px black;
padding:5px !important;
}


/* THREAD TABLE TITLE */
.thead{
text-shadow: 1px 1px 2px black;
}


/* MENU ICON (forum tools, etc) */
img[src*="images/neogaf/misc/menu_open.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 8px !important;
padding-top: 8px !important;
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_menu_icon.png) no-repeat !important;
}


/* LAST POST ICON */
img[src*="images/neogaf/buttons/lastpost.gif"]{
height: 0 !important;
width: 0 !important;
padding-left:12px !important;
padding-top:12px !important;
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_arrow_hipster.png) no-repeat !important;
}


/* THREAD SORTING DOWN Arrow (Beside "Last Post")*/
img[src*="images/neogaf/buttons/sortasc.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 10px !important;
padding-top: 10px !important;
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_downarrow.png) no-repeat !important;
}


/* THREAD SORTING UP Arrow*/
img[src*="images/neogaf/buttons/sortdesc.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 10px !important;
padding-top: 10px !important;
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_upsort_arrow.png) no-repeat !important;
}



/* LAST POST TIME */
.time{
color:#01599B !important;
font-weight:bold;
}


/* NEW THREAD ICON */
img[src*="images/neogaf/statusicon/thread_new.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 25px !important;
background: url(http://dl.dropbox.com/u/23940693/gaf/gaf_thread_new.png) no-repeat !important;
}


/* OLD THREAD ICON */
img[src*="images/neogaf/statusicon/thread.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 25px !important;
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_thread_old.png") !important;
}


/* LOCKED THREAD ICON */
img[src*="images/neogaf/statusicon/thread_lock.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 25px !important;
padding-top: 25px !important;
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_thread_locked.png") !important;
}


/* AVATARS */
img[alt$="Avatar"]{
/*visibility: hidden;*/
}


/* MY AVATAR */
/*img[src*=""]{*/
/*for later use*/
/*}*/


/* USER NAME */
a.bigusername {
font-size:105% !important;
color:#01518E !important;
text-shadow: 0px 1px 0px silver;
}
a.bigusername:hover{
color:#FF8B01 !important;
}


/* NEW THREAD BUTTON */
img[src*="images/neogaf/buttons/newthread.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 99px !important;
padding-top: 26px !important;
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_newthread.png") !important;
}


/* REPLY BUTTON */
img[src*="images/neogaf/buttons/reply.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 99px !important;
padding-top: 26px !important;
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_reply.png") !important;
}


/* VIEW FIRST UNREAD ICON (Thread Sorting) */
img[src*="images/neogaf/buttons/firstnew.gif"]{
height: 0 !important;
width: 0 !important;
padding-left: 11px !important;
padding-top: 7px !important;
background: url("http://dl.dropbox.com/u/23940693/gaf/gaf_downarrow.png") !important;
}


/* FOOTER */
#footer {
height:51px !important;
padding-top:2px;
background: #505050 !important;
}
/*Added*/
#footer-time:before{
/*font-weight:bold;*/
font-size:115% !important;
color:#FF8B01 !important;
Content:"NeoGAF Pro style created by sky";
text-shadow: 1px 1px 2px black;
}
#footer-time{
/*color:#c5c5c5;*/
color:#505050 !important;
}
#footer-time span.time{
/*color:#FF8B01;*/
color:#505050 !important;
}
#footer-nav {
padding-top:15px !important;
background:none !important;
}
#footer-nav ul li a{
color:white !important;
text-shadow: 1px 1px 2px black;
font-style:normal;
font-weight:bold;
}
#footer-nav ul li a:hover{
color:#FF8B01 !important;
}
a[href*="mailto:support@neogaf.com"]:before{
content:url(http://dl.dropbox.com/u/23940693/gaf/gaf_mail_icon.png);
padding:5px;
}
a[href*="#top"]:before{
content: url(http://dl.dropbox.com/u/23940693/gaf/gaf_up_arrow.png);
padding:5px;
}

/* SPOILERS */
.spoiler {
color: black;
cursor: pointer;
}
.spoiler:hover {
background: #000000 !important;
color: #FF8B01 !important;
}

/* QUOTES */
.quotearea:before {
content: /*[[quotearrow]]*/;
position:absolute;
margin-left:-13px;
}

.tfoot {
background: #01518E !important;
}


/* FIREFOX END */
}
