css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnbeta.com"), domain("www.cnbeta.com") {}

@-moz-document url-prefix("http://www.cnbeta.com/articles"), url-prefix("http://cnbeta.com/articles") {

  /* 隐藏不需要的元素 */
  #wrapper > *, #main > *, #newsBox> *, #news > *, #feedback > *, #news_content ins, .digbox, .gbox830 { display: none !important; }
  #main, #content, #news, #news_title, #news_content, #good { display: block !important; }

  /* 去边框 */
  #contentWrapper, #news { border: none !important; }

  /* 黑底 */
  body { background: #000 !important; opacity: 0.9; }

  /* 白背景色 */
  #wrapper, #main { width: 650px !important; background: #fff !important; -moz-border-radius: 15px; -webkit-border-radius: 15px; }

  /* 边距 */
  #wrapper { padding: 10px 20px !important; margin: 20px auto !important; }

  /* 消除浮动 */
  #newsBox, #contentWrapper { float: none !important; width: 100% !important; }

  /* 标题字体大小 */
  #news_title { margin-bottom: 20px !important; font-size: 24px !important; }

  /* 增大内容面积 */
  #news_content { width: 100% !important; font-size: 18px !important; line-height: 1.5em !important; }

  /* 热门评论 */
  #feedback { position: fixed; top: 20px; left: 0; width: 180px !important; height: 600px !important; overflow: auto; -moz-border-radius-topright: 10px; -webkit-border-top-right-radius: 10px; }
  #good { background-color: #aaa !important; border: none !important; font-size: 15px !important; line-height: 1.5em !important; }
  #g_title, .re_author, .re_mark { display: none; }
  #g_content dl { padding: 5px !important; border-color: #888 !important; }
  .re_detail { padding: 0 !important; line-height: 120% !important; }
  
}
