css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.pcinpact.com/news/"),
               url-prefix("http://www.pcinpact.com/breve/"),
               url-prefix("http://www.nextinpact.com/news/"),
               url-prefix("http://www.nextinpact.com/breve/") {

#bloc_commentaires {
  width: 984px !important;
  position: relative;
  z-index: 1;
}

#sidebar {
  position: relative;
}
#sidebar:hover {
  z-index: 2;
}

#bloc_commentaires .bandeau_colore {
  width: 960px !important;
}

#bloc_commentaires .commentaire {
  margin-bottom: 10px !important;
}

#bloc_commentaires .commentaire_entete,
#bloc_commentaires .commentaire_entete_team {
  width: 982px !important;
  box-shadow: 0 -5px 5px -5px rgba(0, 0, 0, 0.5);
}

#bloc_commentaires .commentaire_supprime > div {
  width: 972px !important;
  padding: 2px 5px !important;
}

#bloc_commentaires .commentaire_content {
  width: 885px !important;
}

#bloc_commentaires .pager {
  width: 100% !important;
}

#bloc_commentaires #commentaireFrm_form {
  width: 792px !important;
}
#commentaire_Frm textarea {
  width: 756px !important;
}

.acceuiltest_separateur {
  width: auto !important;
}

}
