css:
@namespace url(http://www.w3.org/1999/xhtml);

/* togetter */
@-moz-document domain("togetter.com") {
   #header
  ,#header+div
  ,#header+div+div
  ,#header+div+div+div
  ,#header+div+div+div+div
  ,.right
  ,.side_box
  ,.advertise_box
  ,.footer_top
  ,#footer
  { display: none !important; }

  #document { width: 630px !important; }
}

/* ラジック */
@-moz-document domain("rajic.2chblog.jp"), domain("rajic.ldblog.jp") {
   #header-bg, #header2, #extra
  ,.blog-title-outer, .menu, .column-inner-2>iframe+div
  ,#container #sub, #rss_recommend
  ,iframe, .column-inner-2>center
  ,.article-body>h4, #lws_0, .related-articles
  ,.article-body>div>span>b>a ,#comment-form
  ,.article-pager-type2, #footer_rss
  ,#trackback-form, #footer
  { display: none !important; }
}

/* ハム速 */
@-moz-document domain("hamusoku.com") {
   .blog-title-outer
  ,#sub
  ,.topbox
  ,.google-user-ad
  ,#extra
  ,.amazlet-box
  ,.article-body>center
  ,.article-body>span
  ,.ham_ranking_div
  ,#footer_custom
  { display: none !important; }
}

/* 痛いニュース */
@-moz-document url-prefix("http://blog.livedoor.jp/dqnplus/") {
   #header
  ,#title
  ,#left
  ,#right
  ,iframe
  ,*[class*="google"]
  ,.pager
  { display: none !important; }

  body { background: #E9E9E9 !important; }
}

/* VIPPERな俺 */
@-moz-document url-prefix("http://blog.livedoor.jp/news23vip/") {
   #header2
  ,.blog-title-outer
  ,#sub
  ,#extra
  ,*[class*="ad-"]
  ,#recent-entry
  ,.pager
  ,a[href*="amazon"]
  ,a[href*="amazon"]+br
  ,a[href*="review"]
  ,a[href*="review"]+br
  ,a[href*="archives"]
  ,a[href*="archives"]+br
  ,.related-articles
  ,.footer-outer
  ,#comment-form
  ,#trackbacks-wrapper
  { display: none !important; }

  div#main { width: inherit !important; }
  div#container { width: 800px !important; }
}

/* ニュー速VIPブログ */
@-moz-document url-prefix("http://blog.livedoor.jp/insidears/") {
   #banner
  ,#links
  ,#content>center
  ,div.main+*+div
  ,div.blogbody>b
  ,div.blogbody>b+font
  ,div.blogbody>font[color="black"]>div
  ,*[class*="google"]
  { display: none !important; }

  body {
    background: url(http://static.tumblr.com/embqol4/BNwlpv2on/bgnoise.gif) !important;
  }
  #content {
    float: none !important;
    margin: 0 auto !important;
    background-color: #FFF !important;
  }
  .blog { margin: 0 10px !important; }
}

/* まめ速 */
@-moz-document domain("mamesoku.com") {
   #head
  ,#globalnavi
  ,.rssoutline
  ,#menuleft
  ,#menuright
  ,#pan
  ,*[class*="google"]
  ,#related
  ,iframe[width="300"]
  ,h3,#ranking
  ,.wareclass_frame
  ,*[class*="side"]
  ,#copyright
  ,#commentform
  ,.rssoutline2
  ,#navi,#pagetop
  { display: none !important; }
}

/* キニ速 */
@-moz-document url-prefix("http://blog.livedoor.jp/kinisoku/") {
   #header, #aside, #tweet_rank, #article_rss,
   #moretop_ad, *[class*="_ad"], #amazon_rank,
   div.article_body+div+h3, #rss_premium, div.related-articles,
   *[id*="ad_"], #pagenav, #footer, address
  { display: none !important; }
  #article { float: none !important; margin: 0 auto !important; }
}

/* らばＱ */
@-moz-document domain("labaq.com") {
   #banner, #left, #right,
  .pager, iframe, .adsection_left, .lq_stb_block,
  .amazlet-box, .amazlet-box+h4, .amazlet-box+h4+ul,
  #lq_st, .navigate, .posted, #outfooter
  { display: none !important; }
  #blogcontainer, #content { background-image: none !important; }
  #blogcontainer { margin: 2em auto !important; }
}

/* アルファルファモザイク */
@-moz-document domain("alfalfalfa.com") {
    #blogcontainer, #hot_tweet, .posted4,
    #left, div[class*="fix"], div.comment
    div.fullbody>div:first-child,
    div.menuXX, div[class*="google"], div#container iframe,
    div.keyword, div.menu7, div[class*="adw"],
    #article_footer_amazon, div.posted3,
    div.menu, div.mv, div.hrr, div.takecommenthead,
    div.amr, #thumbnail_list
    { display: none !important; }
    
    p[class^="r"] { margin-bottom: 20px !important; }
    #container, #wrapper { width: 790px !important; }
    #blog { box-shadow: 0 1px 5px #ddd; }
    #content { margin-top: 2em !important; }
    div.fullbody { padding: 1em !important; }
    div.blogbody { padding: 3em !important; background-color: #F7F7F0 !important; border: 1px solid #E5D9C8; }
}

/* 妹はVIPPER */
@-moz-document domain("vipsister23.com") {
    #column-left, #column-right, #site-nav, #tweet_head,
    div[id*="ad-"], div[class*="ad-"], div[class*="-ad"],
    #share, div#top, script+a, div.amazlet-box, #rewrite-list,
    #cat-recent, #pager-bottom, #site-footer
    { display: none !important; }
}

/* ライフハックちゃんねる弐式 */
@-moz-document domain("lifehack2ch.livedoor.biz") {
  #banner, #globalNavi, .topicPath, #headline,
  #right_a, #topmenu, *[class*="-ad"], ins,
  div.blogbody>h3, #gensen, #hotentry, .wareclass_frame,
  div.blogbody+div
  { display: none !important; }
  #blogcontainer { width: 720px !important; }
}
