/* Colors+Transparency */

/*  Adressbar */
#urlbar {
	background:-moz-linear-gradient(hsla(0, 0%, 100%, .5), hsla(0, 0%, 100%, 0.1)) !important;
	border: 1px solid rgba(0,0,0,0.5) !important;
	text-shadow:white 0 0 6px, white 0 0 6px, rgba(255, 255, 255, .5) 0 1px 0 !important;
}
#urlbar[focused] {
	background:-moz-linear-gradient(hsla(0, 0%, 100%, .8), hsla(0, 0%, 100%, 0.4)) !important;
	border: 1px solid rgba(31,175,230,1) !important;  /* remove this line if you don't like the blue border */
	text-shadow: none !important;
}
#urlbar:hover {
	background:-moz-linear-gradient(hsla(0, 0%, 100%, .8), hsla(0, 0%, 100%, 0.4)) !important;
	text-shadow: none !important;
}
/*  Navigationbar */
#main-window[tabsontop="true"]:not([#browser-bottombox]) #navigator-toolbox > * {
	background: none !important;
}
#main-window[tabsontop="true"] #nav-bar{
	border: none !important;
	background: -moz-linear-gradient(hsla(0, 0%, 100%, .5), hsla(0, 0%, 100%, 0)) padding-box !important;
}
#main-window[tabsontop="true"] .tabbrowser-tabs {
	margin-bottom:1px!important
}
/*  Findbar */
#browser-bottombox{
	background: -moz-linear-gradient(hsla(0, 0%, 100%, .5), hsla(0, 0%, 100%, 0)) padding-box !important;
}
#browser-bottombox > *{
	background: none !important;
}
#browser-bottombox > #FindToolbar{
	border-top: 1px solid rgba(10%, 10%, 10%, .4) !important;
}



/* Text Colors+Shadows */

/*  Navigationbar/Bookmarkbar */
#main-window deck {
	color: black !important; 
	text-shadow:white 0 0 6px, white 0 0 6px, rgba(255, 255, 255, .4) 0 1px 0 !important;
}
/*  non-active tab hovered */
#main-window[tabsontop="true"] .tabbrowser-tab:not([selected]) .tab-text{
	color: black !important; 
	text-shadow:white 0 0 6px, white 0 0 6px, rgba(255, 255, 255, .5) 0 1px 0 !important;
}
/*  non-active tab */
#main-window[tabsontop="true"] .tabbrowser-tab:not([selected]):not(:hover) .tab-text{
	color: black !important; 
    text-shadow:white 0 0 9px, white 0 0 9px, rgba(255, 255, 255, .4) 0 1px 0 !important; 
}
/*  active tab */
#main-window[tabsontop="true"] tab{
	color: black !important; 
	text-shadow:rgba(255, 255, 255, .4) 0 1px 0 !important;
}



