/*Addon Bar - auto hide, fast fade in, slow fade out */

/* Fade in takes 0.5 seconds */
#browser-bottombox>toolbar:hover{
   opacity: 1;
   -moz-transition: opacity  .5s;
}
/* Fade out takes 1.5 seconds after .5 seconds */
#browser-bottombox>toolbar{
   opacity: 0;
   -moz-transition: opacity 1.5s .5s;
}
/* Addon bar style */
#browser-bottombox>toolbar{
   position: fixed;
   bottom: -3px !important;
   right: 0px !important;
   margin-right: 18px !important;
   background: -moz-linear-gradient(hsla(0, 0%, 100%, .9), hsla(0, 0%, 100%, 0))!important;
   -moz-box-shadow: 0 0 6px 0 rgba(0,0,0,.7) !important;
   border: 1px solid rgba(0,0,0,.5) !important;
   border-radius: 0 0 0 0 !important;
}
#addonbar-closebutton{
   display: none !important; 
}
