css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("world1.aquahaze.de"), url("http://aquahaze.de/faq.php"), url("http://aquahaze.de/rules.php") {


/************************************************************************************************************************
										AQUAHAZE - DARK_RED-DESIGN by Demo
										
														
************************************************************************************************************************/

										/*			1. BODY
													2. KOPF
													3. MENUE (LINKS & RECHTS)
													4. MITTE
													5. LINKS
													6. KLEINIGKEITEN				*/

/************************************************************************************************************************

1.		BODY

************************************************************************************************************************/
	body{
		background:-moz-linear-gradient(bottom, #440000, #B40000) !important;
		font-family: Verdana, Arial, Helvetica, sans-serif;
		font-size: 10px;
		color: lightblue;
		font-weight: bold;
		line-height: 12px;
	}
	
	
	/************************************************************************************************************************
	
	2. KOPF
	
	************************************************************************************************************************/
	
	
	#headerimg{
		display:none;
	}
	
	#header{
		background-image: url(http://www.shufggy.lima-city.de/sonstiges/aquahaze/logo.png) !important;
		width: 369px !important;
		height: 83px !important;
	}
	
	
	
	/************************************************************************************************************************
	
	3. MENUE (LINKS & RECHTS)
	
	************************************************************************************************************************/
	
	.menu {
		font-family: Verdana, Arial, Helvetica, sans-serif;
		font-size: 10px;
		color: #FFFFFF;
		font-weight: bold;
		position: relative;
		margin: 0;
		padding: 20px 10px 20px 30px;
		width: 100px;
		border: 6px solid #000 !important;
		-moz-border-bottom-colors: #440000 #5A0000 #710000 #870000 #9E0000 #B40000 !important;
		-moz-border-top-colors: #440000 #5A0000 #710000 #870000 #9E0000 #B40000 !important;
		-moz-border-left-colors: #440000 #5A0000 #710000 #870000 #9E0000 #B40000 !important;
		-moz-border-right-colors: #440000 #5A0000 #710000 #870000 #9E0000 #B40000 !important;
		-moz-border-radius: 1em 4em 1em 4em !important;
		border-radius: 0em 3em 0em 3em !important;
		padding: 5px 5px 5px 15px !important; 
		background:-moz-linear-gradient(bottom, #000, #A40000) !important;
		line-height: 23px;
	}
	
	.menu a{
		padding: 3px 5px !important;
		color: #FFFFFF !important;
		width: 400px !important;
		-moz-border-radius: 25px 10px / 10px 25px;
		border-radius: 25px 10px / 10px 25px;
	}
	
	.menu a:before{
		content: "× " !important;
	}
	
	.menu a:after{
		content: " ×" !important;
	}
	
	
	.menu a:hover {
		text-shadow: 0px 0px 10px #FFFFFF, -1px -1px #3F3F41 !important;
		background-color: #000 !important;
		border-bottom: 1px solid #CC0000 !important;
		text-decoration: none !important;
	}
	
	/************************************************************************************************************************
	
	4. MITTE
	
	************************************************************************************************************************/
	
	#kasten {
		font-family: Verdana, Arial, Helvetica, sans-serif;
		font-size: 10px;
		color: #FFFFFF;
		font-weight: bold;
		position: relative;
		margin: 0;
		padding: 10px 10px 20px 10px;
		width: 370px;
		border: 1px solid #000 !important;
		background:-moz-linear-gradient(bottom, #8E0000, #9C0000) !important;
		-moz-border-radius: 12px;
		-moz-box-shadow: 0 0 5px 5px #710000 !important;
		-moz-border-radius: 80px !important;
	}
	
	#maingame {
		font-family: Verdana, Arial, Helvetica, sans-serif;
		font-size: 10px;
		color: #FFFFFF;
		font-weight: bold;
		position: relative;
		margin: 0;
		padding: 20px 10px 20px 10px;
		width: 400px;
		border: 1px solid #000 !important;
		background:-moz-linear-gradient(bottom, #570000, #8C0000) !important;
		-moz-border-radius: 12px;
		-moz-box-shadow: 0 0 5px 5px #590000;
		-moz-border-radius: 3em 3em 0em 3em !important;
	}
	
	/************************************************************************************************************************
	
	5. LINKS
	
	************************************************************************************************************************/
	
	a {color: #FFFFFF !important;}
	a:link {color: #FFFFFF !important;}
	a:visited {color: #FFFFFF !important;}
	a:hover {color: #FFFFFF !important;}
	a:active {color: #FFFFFF !important;}
	
	/************************************************************************************************************************
	
	6. KLEINIGKEITEN
	
	************************************************************************************************************************/
	
	
	td.blue{
		background-color: #B40000 !important;
	}
	
	td.lobby{
		border-bottom: 1px dotted #000 !important;
	}
	
	td.lobby2{
		border-bottom: 1px dotted #000 !important;
	}
	
	#frame {
		background-color: #850000 !important;
		border: 1px dotted #000 !important;
	}
	
	.frame {
		background-color: #850000 !important;
		border: 1px dotted #000 !important;
	}
	
	input {
		background-color: #850000 !important;
		border: 1px dotted #000 !important;
	}
	
	input:hover{
		-moz-box-shadow: 0 0 5px 5px #710000 !important;
	}
	
	#wideframe {
		background:-moz-linear-gradient(bottom, #660000, #7B0000) !important;
		border: 1px dotted #000 !important;
	}
	
	.wideframe {
		background:-moz-linear-gradient(bottom, #660000, #7B0000) !important;
		border: 1px dotted #000 !important;
	}
	
	.questtable:hover{
		background-color: #940000 !important;
	}
	
	a.skills {
		background-color: #700000 !important;
		border: 1px dotted #000 !important;
	}
	
	.overlib {
		background: url(http://www.shufggy.lima-city.de/sonstiges/aquahaze/hover_bg_full.jpg) !important;
	}
	
	.editprofile:link, .editprofile:visited, .editprofile {
		background: url(http://www.shufggy.lima-city.de/sonstiges/aquahaze/editprofile.png) !important;
	}
	
	.editprofile:hover, .editprofile:active, .editprofile:focus {
		background-image: url(http://www.shufggy.lima-city.de/sonstiges/aquahaze/editprofile_hover.png) !important;
	}
	
	.clanintern:link, .clanintern:visited, .clanintern {
		background: url(http://www.shufggy.lima-city.de/sonstiges/aquahaze/clanintern.png) !important;
	}
	
	.clanintern:hover, .clanintern:active, .clanintern:focus {
		background-image: url(http://www.shufggy.lima-city.de/sonstiges/aquahaze/clanintern_hover.png) !important;
	}


}
