@namespace url(http://www.w3.org/1999/xhtml); @-moz-document domain("awoiaf.westeros.org") {
/* Text Fonts Colors */
body {
   font: 15px/150% Georgia, "Times New Roman", Times, serif !important;
   color: #60493E !important;
}
#left-navigation {
   font-size: 16px !important;
}

#content {
   font-size: 16px !important;
   line-height: 150% !important;
}

/* Portal Text */
div.body ul li a {
   color: #724D28 !important;
}
div.portal h5 {
   color: #36190E !important;
}

/* Recent changes */
table.mw-enhanced-rc{
   font-family: Arial,Helvetica,sans-serif;
}

/* Code Text */
pre, code, tt, kbd, samp {
   font-family: monospace,"Courier New";
}



/* layout Fixes */
#right-navigation { margin-top: 2.2em !important; }
#left-navigation { top: 1.9em !important; }
div.body ul { padding-left: 1.75em !important; }

#content{ margin-left: 10em !important; }
#left-navigation {left: 10em !important; }

/* hide clutter */ 
.portal { display: none !important;}
#p-navigation, #p-tb, #p-lang{
   display: block  !important;
}


/* Background and colors */
body {
   background-image: linear-gradient(bottom, rgb(237,230,218) 33%, rgb(249,246,241) 67%);
   background-image: -o-linear-gradient(bottom, rgb(237,230,218) 33%, rgb(249,246,241) 67%);
   background-image: -moz-linear-gradient(bottom, rgb(237,230,218) 33%, rgb(249,246,241) 67%);
   background-image: -webkit-linear-gradient(bottom, rgb(237,230,218) 33%, rgb(249,246,241) 67%);
   background-image: -ms-linear-gradient(bottom, rgb(237,230,218) 33%, rgb(249,246,241) 67%);

   background-image: -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0.33, rgb(237,230,218)),
	color-stop(0.67, rgb(249,246,241))
   );
}

#mw-page-base, #mw-panel, #footer, .portal.body {
   background-color: transparent !important;
}
#content{
   background-color: #FCFBF9 !important;
}
#mw-head{
   border-top:5px solid #66473D;
}

hr {
  color: #996A5B !important;
  background-color: #BFB885 !important;
}
h1, h2{
  color: #444;
  border-bottom: 1px solid #BFB885;
}

/* Code Text */
pre, code, tt, kbd, samp {
   background: #F2F0DA url("http://i44.tinypic.com/55otar.png") 0 7px;
   border: solid 1px #E8E3B8;
   border-bottom: solid 1px #D9D4A8;
}

#content, #footer, #mw-head-base{
   background-image: none !important;
}
#content{
   border: solid 1px #BFB885 !important;
}
}
