css:
@-moz-document domain('amazon.com') {
#page-wrap {
text-align: center;
background: #E9E9E9;
margin: 0 -20px;
padding: 20px 0 50px;
}

#page-wrap #content, body > #main, #cs-acc-contain, #wrapper, #PageContentContainer, #siteDirectory, #hlb-page-container, #custom-doc, #avod-main-container, body >table, #uwlext_promo, #gbox-dealsC {
text-align: left;
width: 1000px;
margin: 10px auto !important;
border: 1px solid #CCC;
border-width: 0px 1px;
padding: 20px 20px;
background: white;
box-shadow: 0px 0px 10px rgba(0,0,0,0.5);
border-radius: 5px;
}
#avod-buy-box-container center {
text-align: inherit;
width: inherit;
margin: inherit;
border: inherit;
padding: inherit;
background: inherit;
box-shadow: inherit;
border-radius: inherit;
}
#PageContentContainer { margin-left: -830px !important;  }
#main {
margin-top: 10px;
}

#page-footer, body > #navFooter{
box-shadow: 0px 0px 10px rgba(0,0,0,0.5);
margin: 0px -10px;
padding: 20px 0;
text-align: center;
background: #fff;
}
#page-footer #navFooter {
box-shadow: none;
}
#rhf {
clear: both;
width: 1000px;
text-align: left;
margin: 0 auto;
background: #fff;
}

body { background: #e9e9e9; !important }
#navbar{ background: #fff; }

#divsinglecolumnminwidth{
width: 1000px;
margin: 0 auto;
background: white;
padding: 0 20px;
border: 1px solid #CCC;
border-width: 0px 1px;
box-shadow: 0 0 10px rgba(0,0,0,0.5);
}

.bunkBedShifted {
margin: 0 !important;
}

}
@-moz-document domain('amazon.com'), url-prefix('http://www.amazon.com/gp/registry/wishlist') {
body{
background: #fff;
}
}
