/* blog.fefe.de - blau noch dunkler plus
 * author: sebastian lisken at gmx net
 * adapted from: http://userstyles.org/styles/21113/blog-fefe-de-blau-noch-dunkler-v-1
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://blog.fefe.de"), url-prefix("https://blog.fefe.de") {

body {
   color: #bbb;
   background-color: #111;
   margin: 10px 10px 24px 16px;
   min-width: 42em;
   max-width: 47em;
   position: relative;
   font-family: Arial, Univers, Helvetica, sans-serif;
}

h1, h2, h3, h4 {
   
}

h2 {
   font-size: 250%;
   margin: 0 0 0.2em 0;
   text-shadow: 1px 1px #666;
}

h3 {
   padding-bottom: 3px;
   margin: 2.5em 5px 0.5em 15px;
   margin-right: 5px;
   border-bottom: 5px solid #222;
}

body > b, body > *[align="right"], body > *[style="text-align:right"] {
   font-weight: normal;
   font-size: 80%;
   font-style: italic;
   opacity: 0.5;
}

body > b, body > p[align="right"], body > p[style="text-align:right"] {
   position: absolute;
   right: 0;
}

body > p[align="right"], body > p[style="text-align:right"] {
   top: 1em;
}

body > b {
   top: 0.75em;
   display: block;
   text-align: right;
}

body > div[align="center"], body > div[style="text-align:center"] {
   font-weight: bold;
   margin-bottom: 2.75em;
}

p {
   margin: 1em 0;
}

p[u] {
   background-color: #322;
}

a {
   color: #7af !important;
   text-decoration: none !important;
}

a:visited {
   color: #bce !important;
}

h2 a:visited {
   color: #7af !important;
}

a:hover, a:focus {
   text-decoration: underline !important;
}

h2 a:hover, a:focus {
   text-decoration: none !important;
}

body > ul {
   padding-left: 0;
}

body > ul > li {
   font-family: Georgia, "Times New Roman", Times, serif;
   font-size: 88%;
   line-height: 150%;
   color: #fefefe;
   padding: 5px 8px 8px 10px;
   margin-bottom: 0.75em;
   background-color: #1b1b1b;
   list-style-type: none;
   border-radius: 9px;
   -moz-border-radius: 9px;
   border-top: 2px solid #333;
   border-left: 5px solid #fefefe;
   border-bottom: 2px solid #000;
   border-right: 2px solid #000;
}

body > ul > li > p:last-child {
   margin-bottom: 0;
}

body > ul > li > a:first-child {
   color: transparent !important;
   border-bottom: none;
   background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAYAAADED76LAAAAAXNSR0IArs4c6QAAAIVJREFUGNOFzqENwkAARuHvoDuwxskTzECCYABWoAZcE3SxGBQDsAG+AVODxhIsCYrkUIRSw6/f//L4sxDLVGCOtq2bcx8YIGAsO8UyHeIijbpA8TEJYCqYxDKtscNt0DNmDFHhglkfCDJ4YCU7Fj/f4CnYypbtpnl9G7K7YI+qrZtrV/kGgRIhI/abU1wAAAAASUVORK5CYII=) center center no-repeat;
}

li li {
   list-style-type: disc;
   margin-top: 0.33em;
   margin-bottom: 0.33em;
}

li blockquote {
   font-style: italic;
}

}


@-moz-document url-prefix("http://blog.fefe.de/faq") {

body {
   font-family: Georgia, "Times New Roman", Times, serif;
   font-size: 88%;
   line-height: 150%;
   color: #fefefe;
}

h2, h3 {
   font-family: Arial, Univers, Helvetica, sans-serif !important;
   line-height: 120%;
   color: #bbb;
}

h2 {
   text-align: left !important;
   font-size: 284% !important;
}

h3 {
   font-size: 133%;
   font-weight: normal;
   margin-left: 0;
   margin-right: 0;
}

tt {
   font-size: 120%;
}

form {
   margin: 0.5em 0;
}

}


@-moz-document url-prefix("http://blog.fefe.de/rss.xml") {

body {
   background-color: white;
   padding-bottom: 1.5em !important;
   color: black;
}

h3 {
   margin-left: 0;
   margin-right: 0;
}

a, a:visited {
   color: black !important;
   font-weight: normal;
}

}


@-moz-document url-prefix("http://blog.fefe.de/rss.xml?html") {

a {
   color: #09b !important;
}

a:visited {
   color: #068 !important;
}

}
