css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.baidu.com") , domain("www1.baidu.com"){

table+br , table[align="right"] , div#wrapper>table[align="right"] , div#wrapper>table[bgcolor="#f5f5f5"]{
  display: none !important;
}

body>div#out>div#in>div#wrapper>div#container>table{
  width: 49.8% !important;
  height: 116px !important;
  float: left !important;
  border-spacing: 3px !important;
  table-layout: fixed !important;
  overflow: auto !important;
}

td.f {
  background-color: #F0F3FA;
  -moz-border-radius: 8px;
}

}

@-moz-document domain("zhidao.baidu.com") , domain("zhidaocommit.baidu.com"){

div#center{
  width: 99% !important;
}

div#pg{
  width: 100% !important;
  float: left !important;
}

table+br , div#main>div[style="text-align:left;margin:10px 0 0 0;"] , div#new-header>div#main>div#right , div#new-header>div#main>div#center>div[style="width:85%"] , div#new-header>div#main>div#center>br , div#new-header>div#main>div[style="text-align: left; margin: 10px 0pt 0pt;"] , div#qb-ad-cpro , div#ad-brand-down , div[class="ad-fc-down"]{
  display: none !important;
}

div#center>table{
  float: left !important;
  width: 50%;
  border-spacing: 3px !important;
  height: 7em !important;
  overflow: auto !important;
}

.f , .wikif{
  background:  #DBF1D4;
  border: 1px solid #A5DA94;
  -moz-border-radius: 0.5em;
}

div#content_inner{
  margin-right: auto !important;
}

}

@-moz-document domain("mp3.baidu.com"){

div#ecom , div#dMA , div#wall , div[class="top container_20"] , div#bd_asp_t5s{
  display: none !important;
}

}

@-moz-document domain("news.baidu.com"){

div#r{
  max-width: 1200px !important;
  width: 100% !important;
}

table[style="table-layout:fixed;float:right;word-break:break-all;"]{
  margin-top: -60px !important;
}

div#imgNewsList>dl , div#vidNewsList>dl{
  margin-top: 35px !important;
  height: 460px !important;
}

div#imgNewsList>dl>dd , div#vidNewsList>dl>dd{
  background-color: #EFF2FA !important;
  border-width: 1px !important;
  border-style: none solid solid solid !important;
  border-color: #D2D2D2 !important;
}

div#r>table{
  float: left !important;
  width: 50% !important;
  height: 118px !important;
  overflow: auto !important;
  padding: 5px !important;
  border: 3px solid #FFFFFF !important;
  background-color: #EFF2FA !important;
  -moz-border-radius: 8px;
}

table+br , div#DivPost , div#ecomAdDiv , div#r>br , div#ecad{
  display: none !important;
}

table#bdRankTips{
  margin:auto !important;
}
}

@-moz-document domain("image.baidu.com"){

div#pgw{
  height: 3em !important;
}

div#relecom54{
  display: none !important;
}

}

@-moz-document domain("tieba.baidu.com"){

div#frs_banner_ad{
  display: none !important;
}

}
