css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.lastday.jp") {

/* Non-display part */
#footer,
#main-nav,
#splash,
.right,
#sidebar,
.archive-separator,
#fb-root,
#fbComments,
#comments,
#respond,
.post-body ins
{ display: none !important; }

/* Display part */
body,
#site-wrapper,
#header,
#top,
.main,
#main-two-columns,
.left,
#main-content,
.post,
.post-title,
.post-body
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
    padding: 0 0 0 0 !important;
    margin: 0 0 0 0 !important;
}

}
