css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("beyondgrowth.net") {

p, b, li, strong, td, tt, blockquote, #main, #subposts { font-family: Georgia, serif !important; text-align:left; color: #000; line-height: 1.7em !important; }

p, #main, .entry { font-size: 16px !important; }


h1, h2, h3, h4, h5 { font-family: Arial, Helvetica, sans-serif !important;  color: #000 !important; }

a:link { color:#339 !important; }
a:visited { color:#B0B !important; }
a:link, a:visited {font-family: Georgia, serif !important;}


}
