@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("agnes.hu-berlin.de") {
.hu_rahmen_kleinbilder,
.hu_rahmen_logo,
.hu_rahmen2 .hu_breadcrumb
{
display:none !important;
height: 0px !important;
padding: 0px !important;
}
.hu_rahmen2 .hu_logininfo,
.hu_rahmen2 span,
.hu_rahmen_loginlinks > .hu_loginlinks,
.hu_rahmen_languages,
.hu_rahmen_topicsafter_zeile
{
display:block !important;
position:relative !important;
float:left !important;
}
.hu_rahmen_loginlinks
{
float:left !important;
}
.hu_rahmen1
{
background-image:none;
}
.hu_rahmen2
{
top:0px !important;
position:absolute;
width:100%;
}
.hu_rahmen4
{
position:relative !important;
height:24px;
top:6px;
}
.contentmessage
{
display:none !important;
min-height:22px;
top:6px;
font-size:0.7em !important;
line-height:1.1em !important;
position:relative;
}
.hu_loginlinks_wide
{
width:200px !important;
}
}
