css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("otvety.google.ru") {

  * {
    border-radius: 10px;
    font-family: DejaVu Sans !important ;
  }

  body /*, div */,#menu,menu{
    background: hsl(110,50%,70%) !important;
    color: hsl(110,70%,20%) !important;
    border: hsl(110,50%,50%) !important;
    /* text-shadow: 0.1em 0.1em 0.05em hsl(110,30%,70%) !important; */
    font-family: DejaVu Sans !important ;
  }

  label,#label{
    /* background: hsl(110,30%,70%) !important; */
    color: hsl(110,70%,20%) !important;
    /* border: hsl(110,30%,50%) !important; */
    text-shadow: 0.1em 0.1em 0.05em hsl(110,30%,70%) !important;
  } 

  h1,h,h2,h3,h4,h5,b,u,i{
    color: hsl(110,100%,30%) !important;
  }

  a:visited{
    background: hsl(110,60%,70%) !important;
    color: hsl(110,70%,0%) !important;
  }

  a:hover{
    background: hsl(110,60%,10%) !important;
    color: hsl(110,100%,50%) !important;
  }

  a:link, a:active{
    /* background: hsl(110,60%,70%) !important; */
    color: hsl(110,100%,30%) !important;
  }

  input,text,#input,#text,edit,#edit,message,#message,option,#option,select,#select,button,#button{
    background: hsl(110, 30%, 70%) !important;
    color: hsl(110, 70%, 20%) !important;
    border: hsl(110,30%,50%) !important;
    text-shadow: 0.1em 0.1em 0.05em hsl(110,30%,70%) !important;
    font-family: DejaVu Sans !important ;
  }

}
