css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.ecu.edu"),domain("media.lib.ecu.edu") {
  html,body,#cs_control_2,#content,.CS_Layout_TD,#container,td,form,
  #ecuContent, #Links_2nd {
    background: #181818 !important;
    color: #aaa !important;
  }

    #homeMenu, #Features_2nd {
      background: -moz-linear-gradient(top, #FFCD35, #A18121) 
        !important;
      border-radius: 0 0 5px 5px !important;
    }


    #contentright li {
      color: #aaa !important;
    }
}

@-moz-document domain("blackboard.ecu.edu") {
  #contentFrame, #schemePreview, body, body.ineditmode, 
  body.topFrame, div.breadcrumbs, div.paneTabs ul li.active, 
  div.paneTabs ul li, body.wizardBody div.containerWizard {
    background: -moz-linear-gradient(top, #666, #333) 
    !important;
  }

    #contentArea, #schemePreviewBody, .container, 
    .contentPane div.bottomRound b, .inventory, .containerPortal,
    .portal .container {
      background-color: #999 !important;
    }

    input:focus { color: #333 !important; }
 
    .vtbegenerated {
      max-width: 35em !important;
      font-family: Loma, Tahoma, Monaco, sans-serif !important;
    }
    
    .vtbegenerated img {
      padding-right: .5em !important;
    }
}
