css:
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
	/* 
		the code in here only apply to:
			-pages whose URLs start with http://www.tumblr.com
	*/

body{background-image: url(http://images.psxextreme.com/wallpapers/ps3/nebula_873.jpg); background-repeat: repeat; background-attachment:fixed;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: #000012;}

div.l-header .selection_nipple{border-bottom: 11px solid #51519c;}

#auto_pagination_loader{background-color: #000012; background-image: url(http://images.psxextreme.com/wallpapers/ps3/nebula_873.jpg);}

#auto_pagination_loader_loading{color: #000012; text-shadow: 0 0 10px #999;}

#logo:hover{opacity: 0.5;}

#right_column{margin-top: 60px;}

#left_column{margin-top: 60px;}

div.l-header{position: fixed; background-color: #000012; border-bottom-left-radius: 10px; border-bottom-right-radius: 10px; box-shadow:inset 0 0 5px #51519c; background-image: url(http://images.psxextreme.com/wallpapers/ps3/nebula_873.jpg); z-index: 900;}

.radar{display: none;}

#posts .post{box-shadow:inset 0 0 5px #51519c; border-radius: 8px; -webkit-transition: box-shadow 0.5s;}

#posts .post:hover{background-color: #eeeeee; box-shadow:inset 0 0 20px #51519c;}

#posts .post:hover .arrow{margin-left: -1px;}

.arrow {margin-left: 2px;  -webkit-transition: margin 0.5s;}

.permalink {opacity: 0.0;}

.post_avatar{-webkit-transition: -webkit-transform 0.5s;}

}
