css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {
html, body {
	background-color: #EEEEEE !important;
	color: #333333 !important;
	font-family: "Trebuchet MS",Trebuchet,serif;
}
a, .spoiler:hover a, .greeting {
	color: #FF6600 !important;
}
a:hover, .spoiler:hover a:hover, .reflink a:hover {
	color: #0066FF !important;
}
.adminbar, .filesize, .reflink {
	font-size: .8em;
}
.adminbar a, .reflink {
	font-weight: bold;
}
.logo {
	color: #FF6600 !important;
	font-weight: bold;
	text-align: left !important;
}
.sublogo {
	color: inherit !important;
	font-size: .9em !important;
	margin: 0px 5px 8px;
	padding: 0 !important;
	text-align: left !important;
}
.theader, .news {
	background-color: #DDDDDD !important;
	border: solid 1px #CCCCCC;
	color: #2266AA !important;
	font-weight: bold;
	margin-bottom: .5em !important;
	padding: 2px !important;
	width: auto !important;
	border-radius: 5px;
}
.postblock {
	background-color: transparent !important;
	color: #002244 !important;
	font-weight: bold !important;
}
.footer {
	margin: 2em 0 0 0 !important;
	padding-top: 6px !important;
}
.reflink a, .spoiler:hover {
	color: inherit !important;
}
.filesize, .abbrev, .omittedposts, .thumbnailmsg {
	color: #666666 !important;
}
.thumbnailmsg {
	font-size: .7em !important;
}
.filetitle, .replytitle {
	color: #002244 !important;
	font-weight: bold !important;
}
.postername, .commentpostername {
	color: #004A99 !important;
}
.postertrip {
	color: #FF3300 !important;
}
.reply {
	background-color: #DDDDDD !important;
	border: solid 1px #CCCCCC !important;
	color: inherit !important;
	padding: 0;
	margin: 0;
	border-radius: 5px;
}
blockquote {
	margin: .5em .5em .5em 1em !important;
}
blockquote p, .banner {
	margin-top: 0 !important;
}
blockquote p + .unkfunc {
	margin-top: -.5em !important;
}
.reply blockquote {
	margin: .5em !important;
}
.doubledash{
	display: none;
}
hr {
	border-color: #BBBBBB;
	border-style: solid none none none;
}
table, table td {
	border-style: none !important;
}
.nothumb {
	background-color: #FFFFFF !important;
	border-style: dotted !important;
	margin: .3em .5em !important;
}
.highlight {
	background-color: #EEDACB !important;
	border: 2px dashed #EE6600 !important;
	color: #333333 !important;
}
.highlight .filesize {
	margin-left: 20px;
}
.category {
	background-color: #DDDDDD !important;
	border: 1px solid #CCCCCC !important;
	font-family: inherit !important;
	font-size: .9em !important;
	margin-bottom: 9px !important;
	border-radius: 5px;
}
.header {
	background-color: inherit !important;
	border-bottom: 1px solid #CCCCCC !important;
	color: inherit !important;
	font-size: .9em;
	font-weight: normal !important;
	padding: 0px 5px 0px 5px !important;
}
.list {
	background-color: #EEEEEE;
	padding: 0px 5px 5px 5px !important;
	border-radius: 0px 0px 5px 5px;
}
.list div a {
	display: inline !important;
	text-decoration: underline !important;
	padding: 0 !important;
}
.list div a:hover {
	background-color: inherit !important;
}
.spoiler {
	background-color: #CCCCCC !important;
	color: #CCCCCC !important;
}
.spoiler a {
	color: #CCCCCC !important;
}
}
