@-moz-document domain(4chan.org) {

	/*[[logo]]*/
	hr, 
	td.rules,
	.bf,
	blockquote embed,
	a[href*="affiliates.jlist.com"],
	img[src*="http://static.4chan.org/support/"],
	#recaptcha_logo,
	#recaptcha_tagline,
	.pages td:first-of-type,
	.pages td:last-of-type {
		display: none !important;
	}
}
