css:
@-moz-document url-prefix('https://diasp.org/') {
input { background: #333 !important; color: white !important; }
.stream_element { color: #AAA !important; }
#facebox .content { background: #222 !important; }
.creation, .button { background: -webkit-linear-gradient(top, #333, black) !important;background: -moz-linear-gradient(top, #333, black) !important; background: linear-gradient(top, #333, black) !important; color: #CCC !important; }
.notifications_for_day .stream_element:hover, .tag:hover { background-color: #333 !important; }
.conversation_participants { background: #333 !important}
.stream_container { border-left: #555 1px solid !important; }
.stream_element .reshare { border-left: 3px solid #555; }
#publisher { border-bottom: 1px #555 solid !important; }
.stream .stream_element { border-bottom: 1px solid #555 !important; }
h1, h2, h3, h4, h5, h6 {color: #CCC !important; }
.comment_stream {background: #252525 !important; border-radius: 5px !important; }
body { background-color: #111 !important; color: #CCC !important; }
.badge.active { 
background: -webkit-linear-gradient(top, #333, #050505) !important;
background: -moz-linear-gradient(top, #333, #050505) !important; 
background: linear-gradient(top, #333, #050505) !important; }
#notification_dropdown, .dropdown .wrapper { background: #050505 !important; }
#notification_dropdown .header { 
background: -webkit-linear-gradient(left, #111, #333, #111) !important; 
background: -moz-linear-gradient(left, #111, #333, #111) !important; 
background: linear-gradient(left, #111, #333, #111) !important; }
#notification_dropdown .notification_element.unread { background: #333 !important; color: #CCC !important; }
.dropdown.active .wrapper ul > li, .ac_even, .ac_odd, .ac_over { color: white !important; }
.ac_even { background: #020202 !important; }
.ac_odd { background: #050505 !important; }
.ac_over { background: #222 !important; }
ul.left_nav a.aspect_selector:hover, ul.left_nav a.home_selector:hover, ul.left_nav a.tag_selector:hover, ul.left_nav a.element_selector:hover { background-color: #444 !important; }
ul.left_nav li.active > a:not(.sub_selected) { color: #CCC !important; }
#publisher_textarea_wrapper, #publisher_textarea_wrapper textarea, .comment_box, textarea { background: #333 !important; color: white !important;}
#publisher_textarea_wrapper { border: 2px solid black !important; border-radius: 5px !important;}
label:not(.bootstrapped) { text-shadow: 0 1px 1px #111 !important; }
}
