css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anarchistnews.org") {
  body { background-color: #000 !important; }

    .content, .comment, .comment .links, .comment .submitted,
    #mission, #footer, .node-spam-title, .node-spam-content,
    .comment-spam-title, .comment-spam-body {
      background-color:#222 !important;
      color:#aaa !important;
    }

    .meta, .links, .submitted, thead th, tr.even {
      background-color:#111 !important;
      color:#999 !important;
    }

/* tables */

      thead th { border-top-color: #3d4e7f !important; }

      tr.odd, tr.info { background-color: #4D4F51 !important; }

      tr.even, tr.odd, tbody th {
        border-color: #42494D !important; 
      }

    .title2 { background: #006 !important; }

/* links */
    .meta a:link,.content a:link,.author-pane-line a:link,tr.even 
    a:link {
      color: #44f !important;
    }
    .meta a:visited,.content a:visited,.author-pane-line a:visited,
    tr.even a:visited
      { color: #44b !important; }
    .meta a:hover,.content a:hover,.author-pane-line a:hover,tr.even
    a:hover
      { color: #0f0 !important; }

    .links a, .comment a { color: #00f !important; }

    .comment h3.title a { color: #000 !important; }

    .node, .meta {
      border-bottom: 1px solid #006 !important; /* was #066 */
    }

    .posted-on,.author-pane-line,.reply-count,
    h2.comments,.post-title {
      color: #aaa !important;
    }
}
