css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.deuslovult.org") {

  .comment_text,
  .comment_text code,
  .comment_text blockquote,
  .comment_text blockquote p,
  .comment_text blockquote b,
  .comment_text blockquote i,
  .comment_text blockquote del,
  .comment_text blockquote strong {
    font-family: serif !important;
    font-size:16px !important;
  }

  .content blockquote,
  .content,
  .content p,
  .content b,
  .content i,
  .content del,
  .content strong,
  .content blockquote,
  .content blockquote p,
  .content blockquote b,
  .content blockquote i,
  .content blockquote del,
  .content blockquote strong {
    font-family: serif !important;
    font-size:18px !important;
  }

  .content blockquote {
    margin:0 !important;
    padding:1.2em !important;
    padding-bottom:0.5em !important;
  }

  .content blockquote p {
    margin:0 !important;
    padding:0 !important;
    padding-bottom:0.7em !important;
  }

}
