css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {


/* Mad Dev */

#top-bar-bg{
       background-image:-moz-linear-gradient(center bottom, #303030, #131313) !important; border: 1px solid !important;
-moz-border-radius: 5px !important;
-webkit-border-radius: 5px !important; border-top-color: #117099 !important; border-bottom-color: transparent !important; border-right-color: transparent !important; border-left-color: transparent !important 
}

#top-bar ul li, #session {
    border: 1px solid !important; border-bottom-color: #117099 !important; border-top-color: transparent !important; border-right-color: transparent !important; border-left-color: transparent !important; outline:none !important;
    -webkit-transition: all 0.45s ease-in-out !important;
    -moz-transition: all 0.45s ease-in-out !important; }

#top-bar ul li:hover, #session:hover{
    -moz-border-radius: 5px !important;
-webkit-border-radius: 5px !importan
    border-bottom-color: #117099 !important; border-top-color: #117099 !important; border-right-color: #117099 !important; border-left-color: #117099 !important;  box-shadow: 0 0 15px rgba(17, 112,153, 0.5) !important;
    -webkit-box-shadow: 0 0 5px rgba(17, 112, 153, 0.7) !important; 
    -moz-box-shadow: 0 0 5px rgba(17, 112, 153, 0.7) !important;}

.stream-tab.active, .stream-tab.active, .tab-text:hover {background-color: #e7e7e7 !important; -moz-border-radius: 5px !important;
-webkit-border-radius: 5px !important;
    border-bottom-color: #117099 !important; border-top-color: #117099 !important; border-right-color: #117099 !important; border-left-color: #117099 !important;  box-shadow: 0 0 15px rgba(17, 112,153, 0.5) !important;
    -webkit-box-shadow: 0 0 5px rgba(17, 112, 153, 0.7) !important; 
    -moz-box-shadow: 0 0 5px rgba(17, 112, 153, 0.7) !important;}

.stream-tabs, .tab-text, .stream-tabs, ul.stream-tabs {border: 0px solid !important; border-bottom-color: #117099 !important; border-top-color: transparent !important; border-right-color: transparent !important; border-left-color: transparent !important; outline:none !important;
    -webkit-transition: all 0.15s ease-in-out !important;
    -moz-transition: all 0.15s ease-in-out !important;}

}
