css:
* {
  font-family: century gothic !important;
}
body {
  background: url(http://s3.noelshack.com/uploads/images/17182875610593_bg.png) repeat !important;
}
#container {
  background: url(http://s3.noelshack.com/uploads/images/19561580926272_bgcontainer.png) repeat !important;
  position: relative !important;
  bottom: -4px !important;
}
#content {
  opacity : 1 !important;
}
.trow1, .trow2 {
  background: url(http://s3.noelshack.com/uploads/images/18210519565920_bgtr.png) repeat !important;
}
table {
  background: url(http://s3.noelshack.com/uploads/images/18210519565920_bgtr.png) !important;
  border-color: #fff !important;
}
#header {
  background: url(http://s3.noelshack.com/uploads/images/3466927272600_head4_bg.png) repeat-x !important;
}
#panel {
  background: url(http://s3.noelshack.com/uploads/images/12256223055120_wb_bg.png) !important;
  position: relative;
  bottom: -7px;
}
.thead, .tfoot {
  background: #CEE3F6 !important;
  text-shadow: 0 -1px 0 #00516F !important;
    color: black !important;
}
.button {
  background: url(http://s3.noelshack.com/uploads/images/2969577072594_button_bg.png) !important;
}
