css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.kat.ph") {

body { background-color:#000 !important; color:#aaa !important }

.headmainpart { background:#222 none !important }
#navstyle,
.headmenu { background:#222 none !important }

.header_right_part .search { border-color:#aaa !important }

.headmenu ul li a,
#navstyle ul li a, .bodySearch a { color:#aaa !important }

.siteButton.searchButton { background: -moz-linear-gradient(center top , #333 0%, #555 99%) repeat scroll 0 0 transparent !important }
.siteButton span { color:#eee !important; text-shadow:0 1px 0 #666 !important }

table.data tr.odd { background-color:#222 !important }
table.data tr.odd:hover, table.data tr:hover { background-color:#343434 !important }

.torrentFileList tr td,
.sliderbox ul li, #sidebar, .hideSidebar,
table.data tr td, table.data tr th { border-color:#333 !important }

.showmore, .commentsView { background-color:#222 !important; border-bottom-color:#333 !important }

.tagcloud, .tagcloud.faqTags { background:#333 none !important }
.tagcloud a, .namefront a { color:#aaa !important }

.torrentname a { color:#d1d1a1 !important }

.previewcomment { color:#333 !important }

.bbedit-toolbar { background-color:#eee !important; border-radius:3px }

.torrent_files .folder, .torrent_files .folderopen,
.vuze, .vuze:hover { background-color:#444 !important }

.torrent_files span.folder a, .torrent_files span.folderopen a { color:#aaa !important }

.torrentFileList tr:hover { background-color:#222 !important }

.torrentFileList tr.last .torTree { background-color:transparent !important }

#comarea { background-color:#333 !important }
.commentowner { border-color:#333 !important }

.commentbody { background-color:transparent  !important }
select, textarea, .textinput, #adcopy-outer input { border-color:#444 !important }

.qrate { background:#333 none !important; border-radius:3px }

ul.tabNavigation li a.selectedTab, ul.tabNavigation li a.selectedTab:hover { background-color:#000 !important; color:#aaa !important }

.reportedMessage { background-color:#222 !important }

.siteButton, .icon16 { background: -moz-linear-gradient(center top , #333 0%, #444 100%) repeat scroll 0 0 transparent !important }
.partner2Button { background: -moz-linear-gradient(center top , #363 0%, #474 100%) repeat scroll 0 0 transparent !important }

#previewPopupContainer .prevAV, .previewPopupContainer .prevAV
{
   background: -moz-linear-gradient(center top , #444 0%, #555 100%) repeat scroll 0 0 transparent !important;
   border-color:#666 #666 #777 !important
}

}
