css:
@-moz-document domain("forum.shiftdelete.net") {

html {background:url("http://i.imgur.com/jUs1u.gif") !important;}

.toplinks ul.nouser li a {background:#2f2f2f !important;}
.toplinks .logindetails {background:#2f2f2f !important;}
.toplinks ul.isuser li a {color:#000000 !important;}
.toplinks ul.isuser li.welcomelink {color:#000000 !important;}


.body_wrapper {background:#BEBEBE !important;}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {border-left: 1px solid #000000 !important; background: #EBEBEB !important;}


.postbit, .postbitlegacy, .eventbit {border: 0px solid #000000 !important;}
.postbit .posttitle, .postbitlegacy .title {border-bottom: 1px solid #000000 !important;}

.formcontrols .blockrow {background: #EBEBEB none repeat-x center bottom !important;}

.blockbody {background: #EBEBEB !important; no-repeat;}

.blockfoot, .blocksubfoot {background: #EBEBEB !important;}
.vbseo_buttons .vbseo_liked {background-image: url("http://i.imgur.com/WfTfO.png") !important;}


#vB_Editor_001_smiliebox , #vB_Editor_QR_smiliebox {height:260px !important;}



.posthead, #quickreply_title, .blockhead, .searchlisthead, .threadlisthead.table {background:url("http://forum.shiftdelete.net/images/buttons/newbtn_middle.png") repeat-x scroll 0 0 #2f2f2f !important; border:1px solid #000000 !important;}


.above_body {background:none !important;}


#navbar {background:url("http://i.imgur.com/2mkHn.png") repeat scroll 0 0 #2F2F2F !important; }



.threadinfohead.blockhead {background:url("http://forum.shiftdelete.net/images/buttons/newbtn_middle.png") repeat scroll 0 0 #2f2f2f !important; }



#newreplylink_top, #newreplylink_bottom, #newthreadlink_top, #newthreadlink_bottom {background:url("http://forum.shiftdelete.net/images/buttons/newbtn_middle.png") repeat-x scroll left top #2f2f2f !important; }



.forumhead {background:url("http://i.imgur.com/MGr6s.png") repeat-x scroll 0 0 #7192A8 !important; border:1px solid #2e2e2e !important;}



.blockbody ol li div div a, .threadstats.td.alt li a, .threadlastpost.td dd a, .inner h3 a , .author a, #reputationlist li div div a, .usercp_folder-left, .inactive a, .titleline h2 a, .subforum a, .lastposttitle a, .lastpostby div a{color:#555555 !important;}



#navtabs, #navtabs li a.navtab{background:url("http://forum.shiftdelete.net/images/buttons/newbtn_middle.png") repeat-x scroll 0 0 #2F2F2F !important;}

.navtabs li.selected a.navtab{color:#000 !important;}

#navtabs li.selected a.navtab, #navtabs li.selected a.navtab:hover {background:url("http://forum.shiftdelete.net/images/gradients/selected-tab-gradient-with-top-alpha.png") repeat-x scroll 0 0 #2f2f2f !important;}



#navtabs li a.navtab:hover { background: url("http://forum.shiftdelete.net/images/gradients/selected-tab-gradient-with-top-alpha.png") repeat-x scroll 0 -4px #2f2f2f !important; color: #000000 !important;}



#navtabs li a, #navtabs li, #globalsearch form.navbar_search span.textboxcontainer, #globalsearch form.navbar_search span.buttoncontainer, #globalsearch form.navbar_search span.textboxcontainer span, #globalsearch form.navbar_search span.buttoncontainer span{border-left:0px !important; border-top:0px !important; border-right:0px !important; }



li.selected ul.floatcontainer {border-top:2px solid #422326 !important; }



.navbar_search .textboxcontainer input {border:2px solid #422326 !important;}



.navtabs .popupbody {background: none repeat scroll 0 0 #422326 !important; border: 1px solid #422326 !important;}



#sidebar_container a{color:#444444!important;}



.blocksubhead.sortrow{border-bottom:1px solid #2F2F2F !important;}



.blockbody.settings_form_border {border-bottom: 1px solid #2f2f2f !important; border-left: 1px solid #2f2f2f !important; border-right: 1px solid #2f2f2f !important;}



.pmlist li .title {color:#000000 !important;}



.blockbody .blocksubhead {background:url("http://forum.shiftdelete.net/images/buttons/newbtn_middle.png") repeat scroll 0 0 #2F2F2F !important; }









.navtabs li.selected li:hover a.popupctrl,.floatcontainer li.popupmenu a.popupctrl.active, .floatcontainer li.popupmenu a.popupctrl:hover {background-color:#999 !important; border-color:#999 !important;}



.toplinks .isuser li a:hover{background-color:#999 !important; border-radius:3px 3px 3px 3px !important; }



.toplinks ul.isuser .notifications .popupbody {background:none repeat scroll 0 0 #999 !important; border:1px solid #999 !important; }



.toplinks ul.isuser .notifications .popupbody li a:hover , .toplinks .nonotifications .popupbody a:hover {background:none repeat scroll 0 0 #FFEB90 !important; }



.toplinks ul.isuser .notifications .popupbody li a {border-top:0px !important; border-radius:3px 3px 3px 3px !important;}



.toplinks .notifications a.popupctrl, .toplinks .nonotifications a.popupctrl.active {background: url("http://forum.shiftdelete.net/images/misc/arrow.png") no-repeat scroll right center #FDD56F !important; border-radius:3px 3px 3px 3px !important; }





#pagination_top form span a, #below_searchresults form span a,#pagination_bottom form span a {border:1px solid #000 !important; color:#111 !important;}

#pagination_top form span a:hover, #below_searchresults form span a:hover,#pagination_bottom form span a:hover{background-color:#422326 !important; border:1px solid #000 !important; color:#fff !important;}



#pagination_top form span.selected a, #below_searchresults form span.selected a, #pagination_bottom form span.selected a {background:url("http://i.imgur.com/MGr6s.png") repeat-x scroll 0 0 #759FBB !important; color:#fff !important;}



.pollresultsblock .blockhead, .polloptionsblock form.block .blockhead {background:none repeat scroll 0 0 transparent !important; border:0 none !important;}





.blocksubhead.subsectionhead.userprof_headers.userprof_headers_border {background:url("http://i.imgur.com/MGr6s.png") repeat-x scroll 0 0 transparent !important; border:1px solid #2e2e2e !important;}



#usercp_content .cp_content #profileform .formcontrols {border: 1px solid #2f2f2f !important;}



.blockbody ul li .blockrow li a {color:#555555 !important;}



.texteditor.quickedit.formcontrols.forum, .wysiwyg_block .blockbody.formcontrols, .texteditor.quickedit.formcontrols.forum .blockrow .primary.textbox{border-color:#555 !important;}



.block.postbit.automoderated .blockbody.postbody .blocksubhead.title{background:url("http://i.imgur.com/MGr6s.png") repeat-x scroll 0 0 transparent !important; border-top:none !important;}



}
