css:
@-moz-document 
/* http://armanam.net/ */

url-prefix(http://www.google.com/reader),
url-prefix(https://www.google.com/reader),
url-prefix(http://www.google.com/reader/shared),
url-prefix(https://www.google.com/reader/shared) {


a:hover {
    color: #CC6600 !important;
}
.entry .entry-body  {
    font-family: 'Geeza Pro', tahoma !important;
    font-size: 12px !important;
    line-height: 21px;
}
.entry-annotation-body {
    font-family: 'Geeza Pro', tahoma !important;
    font-size: 11px !important;
    font-style: normal !important;
    line-height: 18px !important;
    padding: 35px 10px 5px !important;
}
#entries.list .entry .collapsed, #entries.list .read .collapsed  {
    font-family: 'Geeza Pro', tahoma;
    font-size: 11px;
}
.entry-annotation-box .round-box .c {
    background: #efefef !important;
    border: 1px solid #e2e2e2;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    min-width: 350px;
}

#entries.list .entry .collapsed {
    line-height: 20px !important;
    padding: 0 !important;
}
#entries.list .collapsed .entry-icons, #entries.list .collapsed .entry-main .entry-source-title, #entries.list .collapsed .entry-secondary {
    top: 0 !important;
}
#entries.list .collapsed .entry-main .entry-original {
    top: 3px !important;
}
#entries.list .collapsed .entry-secondary .entry-title {
    font-weight: bold;
    line-height: 18px !important;
    font-size: 12px;
}
.entry-secondary-snippet {
    line-height: 18px !important;
}
#title-and-status-holder {
    margin-right: 0 !important;
}
#entries {
    padding: 0 !important;
    border-top: 1px solid #C2CFF1 !important;
}
#nav, .lhn-section, .scroll-tree li, #viewer-header-container {
    background: #f6f6f6 !important;
}
#title-and-status-holder {
    background: #EEF1FE;
}
#viewer-container {
    border: 2px solid #ddd !important;
    background: #EBEFF9 !important;
}

#viewer-header {
background: #C2CFF1 !important;
height: 34px !important;
margin-right: 0 !important;
}
#lhn-add-subscription-section {
height: 57px !important;
}

.jfk-button, .goog-button-base-inner-box {
height: 20px !important;
line-height: 20px !important;
}
.jfk-button-standard {
border: 1px solid #AAA !important;
background: #F9F9F9 !important;
}

.goog-button-base, .jfk-button, .goog-inline-block, .goog-flat-menu-button {
cursor: pointer !important;
}
#top-bar {
height: 50px !important;
}
.goog-flat-menu-button {
line-height: 20px !important;
}
.goog-button-tight .goog-button-base-content {
line-height: 1em !important;
padding-top: 4px !important;
}
.goog-flat-menu-button-dropdown, .goog-menu-button .goog-menu-button-dropdown {
top: 8px !important;
}
.jfk-textinput {
height: 18px !important;
}
#viewer-top-controls {
padding: 2px 5px 3px !important;
}

}
