@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.ubuntu.ru") {
body {
    font: 88%/130% "Ubuntu Beta",UbuntuBeta,Ubuntu,"Bitstream Vera Sans","DejaVu Sans",Verdana,arial,helvetica,sans-serif!important;
    background-image: url(data:image/png;base64,R0lGODdhPAA8AIABAM7Kxf///ywAAAAAPAA8AAACjERueJG8DZ9U1NW2st68+w+G4kiW5hlG12S1qsu+GErX9o3nuRz36w8D6obEovHo4QGVwSYTCY1Kpx+m1efEzqjcrhd3XWrHwq/5jE6Ss+J2Mw2Pm8PsOn0rz+uN93W/vBcoaPNn54c1mKhIUth4uAgZ2eHoRil5KWmpiYjZObhZ+eg5GghqGEqamlcAADs=)!important;
}
div#__container > div:first-child {
    max-width: 100%!important;
}
#__search-box {
    float: left!important;
    margin-left: 16px!important;
}
#search__input {
    background: #fff;
}
.smf .poster {
    text-align: left!important;
}
.smf .poster h4 {
    margin: 0.2em 0 0.4em 1.3em!important;
}
#__header-menu {
    border-radius: 0!important;
}
#__header-menu .header-submenu > div {
    margin-left: 226px!important;
    margin-right: 32px!important;
}
#__wrapper {
     box-shadow: none!important;
     border: #EBEBEB 4px solid;
     border-width: 0 4px 4px;
}
#__main {
    color: #575757!important;
    padding: 0 12px 12px!important;
}
#__header-menu > ul li a {
    text-shadow: none!important;
    font-size: 16px!important;
    padding: 24px 14px 0 14px!important;
}
#__header-menu .header-submenu a {
   font-size: 12px!important;
   padding: 13px 12px 0 12px!important;
}
.smf #upper_section {
    border: 1px solid #E3E3E3!important;
}
.smf .buttonlist ul li a {
    text-transform: none!important;
    border-radius: 5px 0 0 5px;
    background:#DADADA!important;
}
.smf .buttonlist ul li a span {
    height: 20px!important;
    margin-bottom: 6px!important;
    border-radius: 0 5px 5px 0;
    background:#DADADA!important;
}
ul.dropmenu li a {
    font-size: 12px!important;
}
ul.dropmenu li a:hover, div.buttonlist a:hover {
    color: #FFF!important;
}
.smf .buttonlist ul li a.active {
    color: #FFFFFF!important;
    background:#2C001E!important;
}
.smf .buttonlist ul li a:hover, .smf .buttonlist ul li a:hover span,.smf .buttonlist ul li a.active:hover, .smf .buttonlist ul li a.active:hover span {
    background:#FD9604!important;
}
.smf .buttonlist ul li a.active span {
    color: #FFFFFF!important;
    background: #2C001E!important;
}
.smf .buttonlist ul li a:active {
    position: relative;
    top: 1px;
}
.smf .codeheader, .smf .quoteheader {
    font-size: 10px!important;
}

.smf .topic_table td.subject span a {
    font-size: 15px!important;
    text-decoration: underline!important;
    line-height: 22px!important;
    /*padding-bottom: 4px!important;*/
    display: inline-block;
}
.smf .topic_table td.subject p,.smf .topic_table td.subject p a {
    color: #727070!important;
    
}
.smf .topic_table td.subject p {
    padding-left: 8px!important;
}
.smf .keyinfo h5 a {
    text-decoration: underline!important;
}
.smf .stickybg2 {
    background-color:#F3F3F3!important;
}

th.last_th {
    width: 220px!important;
}
.smf table.table_grid td {
    padding: 3px 7px!important;
}
.smf .table_list tbody.content td.info a.subject {
    color: #DD4814!important;
    text-decoration: underline!important;
}
.smf a:link, .smf a:visited,.smf .windowbg2 {
    color: #575757!important;
}

.smf h3.catbg {
    font-size: 14px!important;
}
.smf a:hover {
    color: #DD4814!important;
}
.smf .catbg, .smf .catbg2, .smf tr.catbg td, .smf tr.catbg2 td, .smf tr.catbg th, .smf tr.catbg2 th {
    background: #EFEFEF !important;
    color: #575757!important;
    line-height: 30px!important;
}
.smf tr.catbg th.first_th,.smf div.cat_bar {
    background: #EFEFEF !important;
}
.smf #forumposts div.cat_bar, .smf #forumposts div.cat_bar h3 {
    background: transparent!important;
}
.smf #forumposts div.cat_bar h3 {
    font-size: 16px!important;
}
.smf tr.catbg th a:link, .smf tr.catbg th a:visited, .smf tr.catbg2 td a:link, .smf tr.catbg2 td a:visited {
    color:  #757479!important;
}
.smf .table_grid tr.catbg, .smf .table_grid tr.titlebg {
    border-bottom: 2px solid #FFF!important;
}
td.info p,td.stats {
    font-size: 11px!important;
}
.children a {
    text-decoration: underline!important;
    font-style: italic;
}
.smf .poster h4 {
    font-size: 16px!important;
    font-weight: bold!important;
}

td.lastpost {
    font-size: 12px!important;
}
.smf .windowbg, .smf #preview_body, .smf #upper_section {
    background-color: #EFEFEF!important;
    color: #575757!important;
}
.smf .windowbg span.topslice span,.smf .windowbg span.botslice span {
    background-color: #EFEFEF!important;
}
span.topslice span {
    border-top: #fff 0px solid;
}
.smf .windowbg2 span.topslice span,.smf .windowbg span.topslice span,.smf .windowbg span.botslice span,.smf .windowbg2 span.botslice span {
    border-radius: 0!important;
}
.smf .stickybg {
    background: #E8E8E8!important;
}
.smf .locked_sticky, .smf .locked_sticky2,.smf .locked_sticky a, .smf .locked_sticky2 a {
    font-weight: normal!important;
}
.smf .inner,.smf .signature, .smf .custom_fields_above_signature,.smf hr, .smf .hrcolor  {
    border-top: 1px dotted #C4C4C4!important;
}
.smf hr, .smf .hrcolor  {
    background: transparent!important;
}
.spoilerheader {
    margin-top: 5px!important;
    font-size: 10px!important;
}
.spoilerbutton {
    font-size: 10px!important;
    padding: 2px 8px!important;
}
div.user, .smf .description, .smf .description_board, .smf .plainbox {
    color: #757479!important;
    background: transparent!important;
    border: 0!important;
    border-radius: 0!important;
}

div.user a {
    text-decoration: underline!important;
    font-size: 13px!important;
}
#__main-title h1 {
    margin: 8px 8px 8px 8px!important;
    font-size: 20px!important;
    color: #757479!important;
}
#__main-title #upshrink {
    padding-top: 18px!important;
}
.smf div.title_barIC,.smf div.title_barIC h4 {
    background: #E0E0E0!important;
}
.smf div.title_barIC {
    border-radius: 4px;
}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    background: url(data:image/gif;base64,) no-repeat scroll 0.1em 0.1em transparent!important;
    border-bottom: 2px solid #DEDEDE!important;
    border-top: 2px solid #DEDEDE!important;
    color: #9C9C9C!important;
    font-size: 12px!important;
    line-height: 1.4em!important;
    margin: 0.2em 0 1em 0!important;
    overflow: auto!important;
    padding: 1.2em 1.6em!important;
}
blockquote.bbc_standard_quote {
    background-color: #FFFFFF!important;
}
blockquote.bbc_alternate_quote {
    background-color: #F7F7F7!important;
}
code.bbc_code {
    background: none repeat scroll 0 0 #FFFFFF!important;
    border-bottom: 2px solid #DEDEDE!important;
    border-top: 2px solid #DEDEDE!important;
    display: block!important;
    font-family: "dejavu sans mono","monaco","lucida console","courier new",monospace!important;
    font-size: 12px!important;
    line-height: 1.5em!important;
    padding: 8px 1em!important;
    margin: 0.2em 0 1em 0!important;
    white-space: pre!important;
}
/*images*/
.smf ul.quickbuttons li.quote_button {
    background: url(data:image/png;base64,) no-repeat scroll 0 3px transparent!important;
}
.smf .topic_table td.stickybg2,.smf .topic_table td.locked_sticky2 {
