css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.giga.de") {

/* Hintergrund */
#G08a {
  background: #E5E5E5 !important;
  margin: 0;
  padding: 0;
  font-family: Arial, Verdana, Geneva, Helvetica, sans-serif;
  font-size: 14px;
}

/* Navi Bar mit Popunders */
.neonav {
  visibility: hidden !important;
  height: 0px !important;
}

/* Rubriken Image Navi */
.forumrubrikennavi {
  visibility: hidden !important;
  height: 0px !important;
}

/* Crumbs = Header mit Forenlinks */
.breadcrumb {
  background: #e5e5e5 !important;
}

.breadcrumb li a {
  color: #273d4b !important;
}

.lastcrumb {
  background: #e5e5e5 !important;
}

.lastcrumb ul li {
  color: #273d4b !important;
}

/* Post-Header Bar */
.thead {
  color: #FFFFFF !important;
  font: bold 11px Arial, Verdana, Helvetica, sans-serif;
  padding: 7px 4px;
  background: #7e858b !important;
}

/* TD mit Userinfos */
.alt2, .alt2Active {
  background: #e4e9ef !important;
  color: #434343;
}

}
