style_setting_options.value:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#addon-bar, #FindToolbar {
background:-moz-linear-gradient(rgba(255,255,255,0), rgba(200,200,200,0))!important;
}

#addon-bar, #status-bar, #addon-bar, #browser-bottombox {
outline:none!important;
border:none!important;
}

#addonbar-closebutton {
display:none!important;
}

#browser-bottombox {
position:fixed!important;
bottom:0px;
right:17px;
background:transparent!important;
visibility:hidden!important;
}

#addon-bar > toolbarbutton, #addon-bar > toolbaritem, #addon-bar statusbar > statusbarpanel {
padding: 1px;
min-height:26px!important;
max-height:26px!important;
height:26px!important;
vertical-align: middle;
background-image:-moz-linear-gradient(rgba(255,255,255,0), rgba(200,200,200,0))!important;
}

#addon-bar {
opacity:0.0!important;
border-top:1px solid #A3A3A3!important;
border-right:1px solid #A3A3A3!important;
border-bottom:1px solid #A3A3A3!important;
border-left:1px solid #A3A3A3!important;
position:relative;
-moz-transition:bottom 0.25s 0.5s ease-in, opacity 0.1s 0.65s ease-in, right 0.01s 0.75s!important;
visibility:visible!important;
-moz-border-radius: 0px!important;
background-image:-moz-linear-gradient(rgba(255,255,255,1), rgba(200,200,200,1))!important;
}

#addon-bar:hover {
opacity:1!important;
-moz-transition:bottom .25s .25s ease-out, opacity 0.1s 0.25s ease-out!important;
visibility:visible!important;
}

#addon-bar box {
border:none!important;
}

#addon-bar > * > * > * {
background-color:transparent!important;
}

#addon-bar toolbarbutton:hover, #addon-bar statusbarpanel:hover, #addon-bar toolbaritem:hover {
background:-moz-linear-gradient(rgba(219,221,223,1), rgba(163,163,163,0.4))!important;
border:none!important;
outline:none!important;
}

#FindToolbar {
position:fixed!important;
left:1px;
z-index:1;
visibility:visible!important;
border:1px solid #a3a3a3!important;
bottom:0px!important;
background-image:-moz-linear-gradient(rgba(255,255,255,1), rgba(200,200,200,1))!important;
opacity:1;
}

#FindToolbar:hover, #addon-bar:hover {
z-index:1;
}

#main-window[sizemode="fullscreen"] #addon-bar {
opacity:0.0!important;
border:1px solid #a3a3a3!important;
position:relative;
-moz-transition:bottom 0.25s 0.5s ease-in, opacity 0.1s 0.65s ease-in, right 0.01s 0.75s!important;
visibility:visible!important;
}

#main-window[sizemode="fullscreen"] #addon-bar:hover {
opacity:1!important;
bottom:0px!important;
-moz-transition:bottom .25s .25s ease-out, opacity 0.1s 0.25s ease-out!important;
}

#main-window[sizemode="fullscreen"] #FindToolbar {
left:0px;
}

