css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

Body{background: url('http://img803.imageshack.us/img803/8630/damask.png') top right #000000 repeat fixed !important; }
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#333333!important; }
*{text-shadow:none !important; color:#7a7a7a !important;}

ol#posts li.notification{background-color: #f9f9f9 !important; border-bottom: 2px solid #1e869b !important; color: #f9f9f9 !important;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#f9f9f9;}
#nav .nav_item.active{background-color:#f9f9f9}
#left_column #posts .post .permalink{background-color:#f9f9f9;}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #1e869b !important;}
.notification_type_icon{display:none !important}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: #A099A1 !important; 
	background: #e9e9e9 !important;
        border-top-left-radius: 7px !important;
        border-top-right-radius: 7px !important;}
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#e9e9e9 !important;  
	background-image:none !important;
	border:0px !important}
div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#e9e9e9 !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important}
.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}
#auto_pagination_loader
	{background: #A099A1 !important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 20px #1e869b !important;
	border-right: 10px solid transparent !important;
	border-left: 10px solid transparent !important;}
#left_column #posts .notification {
background-color:#d8d8d8 !important;
color:#666 !important;
border-bottom-color: #1e869b !important}
#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}
#left_column #posts .post .sub_avatar_inner {
-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;}
#new_post_notice_container{
color:#fff !important}
#new_post_notice_container a{
color:#fff !important}
.permalink {background: none !important;
width: 16px !important;
height: 16px !important;
  background: #f9f9f9 !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 10px !important;
  margin-top: 3px !important;
  margin-right: 4px !important;}

}
