/************************************************************************/
/*        LiveMotion Compact - By Repflez - (c)2011-2012 Repflez        */
/************************************************************************/
/* lm_compact.css                                                       */
/************************************************************************/
@namespace url(http://www.w3.org/1999/xhtml);
	
@-moz-document url-prefix("http://www.livemotionweb.com")  {
/************************************************************************/
/* CORE ELEMENT STYLES */
a,a span {
	-webkit-transition: color 0.2s ease-in-out 0s;
	-moz-transition: color 0.2s ease-in-out 0s;
	-o-transition: color 0.2s ease-in-out 0s;
	-ms-transition: color 0.2s ease-in-out 0s;
	transition: color 0.2s ease-in-out 0s;
}

hr {
	margin: 0 !important;
}

span.corners-top,span.corners-bottom,span.corners-top span,span.corners-bottom span,p.right {
	display: none !important;
}

#page-footer {
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}

p.pagination {
	float: none !important;
}

.headerbar span {
	/* The logo has some problems with span when hidden. Fix it.*/
display: block !important;
}

img[src*="http://dl.dropbox.com/u/45957203/Head.png"] {
	/* Let's make the entire logo clickable */
padding-right: 380px !important;
}

div.navbar {
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
	height: 30px !important;
}

/************************************************************************/
/* LAYOUT */
.forumbg,.forabg {
	-webkit-box-shadow: 0 0 30px #000000;
	box-shadow: 0 0 30px #000000; /* Box Shadows */
}

#skinselector form {
	/* Fix for Skin Selector */
width: 0px;
	height: 0px;
}

/* Forum Rows */
.row1,.row2,ul.forums {
	background-color: #d7dff5 !important;
}

ul.topiclist li.header dl dd.dterm .table-title h2,ul.topiclist li.header dl dd,ul.topiclist li.header dl dt,table.table1 thead th,table.table2 thead th {
	color: white !important;
	font-weight: 800 !important;
}

ul.topiclist li.header dl dd.dterm .table-title h2 {
	font-weight: normal !important;
}

li.row,.left-box {
	border-top: none !important;
}

li.row:last-child {
	border: none !important;
	margin-bottom: 5px !important;
}

ul.topiclist dd:first-child {
	border-left: none !important;
}

/* User CP */
#cp-main .panel.sig {
	background: transparent !important;
}

/* Link to watch thread */
.noprint p.right {
	display: block !important;
}

/************************************************************************/
/* BLOG STYLES */
.blog_cal-border {
	background: url("http://i38.servimg.com/u/f38/15/78/66/50/caldar10.png") no-repeat scroll transparent !important;
	border: none !important;
}

.blog_cal-content {
	background: transparent !important;
	float: left !important;
	width: 47px !important;
	font: 150%/100% Arial, Helvetica, sans-serif;
	color: white !important;
}

.blog_cal-day {
	display: block !important;
	font: 50% Arial, Helvetica, sans-serif !important;
	height: 11px !important;
	padding-top: 1px !important;
	text-align: center !important;
}

.blog_cal-month {
	font: 100%/100% Arial, Helvetica, sans-serif !important;
	padding-bottom: 1px !important;
	text-align: center !important;
}

.blog_cal-year {
	display: block !important;
	font: 50% Arial, Helvetica, sans-serif !important;
	height: 12px !important;
	padding-bottom: 1px !important;
	text-align: center !important;
}

/************************************************************************/
/* GENERAL CONTENT */
.postprofile dt a img,.blog_comment-avatar img {
	border: 1px solid rgba(0, 0, 0, 0.5) !important;
	border-radius: 12px;
	background: rgba(0, 0, 0, 0.25) !important;

	-webkit-box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5);
	box-shadow: 0 2px 6px rgba(0, 0, 0, 0.5); /* Limit avatars by the max size allowed by the forum */
max-width: 150px !important;
	max-height: 300px !important;
}

.postprofile dt a img:hover,.blog_comment-avatar img:hover {
	border-color: #7d7d7d !important;
}

/* Remove the porn avatars */
.postprofile dt a[href="/u20"] img {
	display: none !important;
}

/* Online */
.post.online {
	border-top: 2px solid #458b00;
	background: #f8fff1 !important;
}

/************************************************************************/
/* GENERIC REPEATED STYLES */
/*Signature*/
.signature_div {
	border-top: 1px dashed #6b87d9; /*border-left:  1px dashed @borderColor;
	*/
background: #bcc9ee !important;
	min-height: 250px;
	padding: 0 10px 10px !important;
}

/* Profile in posts */
.postprofile {
	background: transparent !important;
	border: none !important;
	float: none !important;
}

.postprofile dd span {
	color: black !important;
	font-weight: bold;
}

.postprofile dt span {
	color: black !important;
	font-weight: bold;
}

/* Posts */
.post {
	padding: 0 !important;
	border: 1px solid #6b87d9;
	border-bottom: none;
	margin: 0 !important;
	background: #e5eaf8 !important;
}

.post .inner {
    margin-top: -5px !important;
}

.postbody {
	padding-top: 0.5em !important;
	background: #d7dff5 !important;
	min-height: 30em !important;
	float: right !important;
}

.postbody .content {
	padding-bottom: 10px !important;
	padding-right: 10px !important;
	margin-left: 10px !important;
	overflow: visible !important;
}

.postbody ul.profile-icons {
	margin-right: 0 !important;
}

.postbody p.author {
	margin-left: 5px !important;
}

#preview .postbody .content {
	padding-bottom: 0 !important;
	padding-right: 0 !important;
}

#textarea_content {
	margin-top: 3px !important;
}

/* Quotes */
.content blockquote {
	border-radius: 10px;
	border: 1px solid #6b87d9 !important;

	background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.1) 100%), #bcc9ee !important;
	background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.1) 100%), #bcc9ee !important;
	background: -o-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.1) 100%), #bcc9ee !important;
	background: -ms-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.1) 100%), #bcc9ee !important;
	background: linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.1) 100%), #bcc9ee !important;

	-webkit-box-shadow: 0 2px 10px rgba(107, 135, 217, 0.6);
	box-shadow: 0 2px 10px rgba(107, 135, 217, 0.6);
}

.content blockquote div {
	margin: 5px 10px !important;
}

/* Portal Bookmarks */
#bookmarks {
	display: none !important;
}

/* Portal & Comments */
#blog_comments {
	border: 1px solid #6b87d9;
	border-bottom: none;
	border-top: none;
	width: 100% !important;
}

#blog_comments h1.page-title {
	border-top: 1px solid #6b87d9;
	background: #D7DFF5 !important;
	margin: 0 !important;
	text-align: center;
	text-shadow: none !important;
	padding: 5px !important;
}

#blog_comments .postbody {
	width: 100% !important;
	min-height: 10em !important;
}

#blog_comments .postbody .content {
	padding-left: 50px !important;
	padding-bottom: 10px !important;
	display: inline !important;
}

#blog_comments .module {
	padding-bottom: 10px !important;
	border-top: 1px solid #6b87d9;
	margin: 0 !important;
}

/* "Modules" */
.module .inner {
	padding-top: 5px !important;
}

}

/************************************************************************/
/* PAGE LAYOUT FIXES */
@-moz-document url-prefix("http://www.livemotionweb.com/u"),url-prefix("http://www.livemotionweb.com/post"),url-prefix("http://www.livemotionweb.com/privmsg") {
	span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span, p.right, .left-box  {
	 display: block !important;
}

/* SEARCH FIXES */
}

 @-moz-document url-prefix("http://www.livemotionweb.com/search") {
	ul.topiclist dt, ul.topiclist dd.dterm  {
	 width: 60% !important;
}

dd.lastpost {
	width: 15% !important;
}
}
