@namespace url(http://www.w3.org/1999/xhtml);

/* Yahoo!ニュース(dailynews) */
@-moz-document domain("dailynews.yahoo.co.jp") {

  #header, #sub, #footer,
  #backToPagetop, #detailRelatedInfo,
  div[id*="center"],div[id^="ad"]
  { display:none !important; }

  #mainMargin {
    width:800px !important;
    margin: 2em auto !important;
  }
  * {
    font-family:'ヒラギノ角ゴ Pro W3','Hiragino Kaku Gothic Pro','メイリオ',Meiryo,'ＭＳ Ｐゴシック','MS PGothic',sans-serif !important;
  }
}

/* Yahoo!ニュース(zasshi) */
@-moz-document domain("zasshi.news.yahoo.co.jp") {

   #header
  ,#contents-header
  ,#sub
  ,div.ynZasshiInfo
  ,#ynSocialBookmark
  ,div.ynZasshiRelatedArticleList+div
  ,#pos-sqb+div
  ,#contents-footer
  ,#footer
  ,div[id*="center"],div[id^="ad"],div[id*="sqb"]
  { display:none !important; }

  #contents #main .yjXL {
    line-height: 1.5 !important;
  }
  #wrapper {
    width:40em !important;
    min-width:40em !important;
    margin: 2em auto !important;
  }
  #main-wrapper,#main { margin:0 !important; }

  * {
    font-family:'ヒラギノ明朝 Pro W3','Hiragino Mincho Pro','メイリオ',Meiryo,'ＭＳ Ｐゴシック',sans-serif !important;
    text-shadow: 0px 1px 0px rgba(255,255,255,.9) !important;
    letter-spacing: -0.02em !important;
  }
  body { background: none repeat scroll 0 0 #FFF9EF; }
}
