css:
@-moz-document domain('stackoverflow.com') {

.post-tag{
color:#3E6D8E !important;
border-bottom:0px dotted #3E6D8E !important;
border-right:0px !important;
margin:2px 2px 2px 0 !important;
padding:3px 4px 3px 4px !important;
text-decoration:none !important;
font-size:80% !important;
line-height:2.2 !important;
white-space:nowrap !important
}

.post-tag:hover{
background-color:#3E6D8E !important;
color:#E0EAF1 !important;
border-bottom:0px solid #37607D !important;
border-right:0px solid #37607D !important;
text-decoration:none !important}

.summary .post-tag {
background-color:#FFF !important;
color:#BBB !important;
}

.summary .post-tag:hover {
background-color:#3E6D8E !important;
color:#E0EAF1 !important;
}

.stats {
float: right !important;
}

.nav .primarynav li a{
color:#888 !important;
background:#FFF !important;
border-top:1px solid #EEE !important;
border-bottom:2px solid #EEE !important;
display:block !important;
float:left !important
}

.nav .primarynav li a:hover {
color:#fff !important;
background:#f90 !important;
}

.narrow .votes{
background:#FFF !important;
}

.answered{
background:#FFF !important;
color:#75845C !important
}

.answered strong {
background:#FFF !important;
color:#75845C !important
}

.answered-accepted{
background:#75845C !important;
color:#FFF !important
}

.answered-accepted strong{
color:#FFF !important
}



.newuser{
color:#555 !important;
background-color:#e1e9d3 !important
}

.newuser h4{
color:#555 !important
}

.newuser a{
color:#555 !important

}

.subtabs a { font-size: 79% !important; }
.views { padding-top: 4px !important; }
.item-multiplier { font-size: 79%; !important }
.question-summary:hover { background: #ECE9D8 !important; }
.nav li { background: white !important; }
.nav a { color: gray !important; }
.narrow .votes { margin-right: 10px !important; }
